import { useSelector, useDispatch } from 'react-redux';
import {RouterProvider, createBrowserRouter} from 'react-router-dom';
import HomePage from './pages/HomePage';
import ItemPage, {loader as itemsLoader} from './pages/Items';
import EventDetailPage from './pages/EventDetail';
import NewFabricPage from './pages/NewFabric';
import EditFabricPage from './pages/EditFabric';
import FabricsRootPage from './pages/FabricsRoot';
import FabricsPage,{loader as fabricsLoader} from './pages/Fabrics';
import FabricDetailPage,{loader as fabricDetailLoader, action as deleteFabricAction} from './components/FabricDetail';
import {action as manipulateFabricAction} from './components/FabricForm';
import CartPage from './components/Cart';
import RootLayout from './pages/root';
import ErrorPage from './pages/Error';
import Authentication from './pages/A'
import { Fragment, useEffect } from 'react';
import Cart from './components/Cart';
import Notification from './components/notification';
import { sendCartData, fetchCartData } from './Store/cart_actions';

let isInitial = true;

const router=createBrowserRouter([
    {
    path:'/',
    element:<RootLayout/>,
    errorElement:<ErrorPage/>,
    children:[
    {
        index:true,
        element:<ItemPage/>,
        loader:itemsLoader
    },
    {
        path:'items',
        element:<ItemPage/>,
        loader:itemsLoader
    },

    {
        path:'fabrics',
        element:<FabricsRootPage/>,
        children:[
         {
            index:true,
            element:<FabricsPage/>,
            loader:fabricsLoader,
         },
         {
            path:':fabricId',
            id:'fabric-detail',
            loader:fabricDetailLoader,
            children:[
                {
                    index:true,
                    element:<FabricDetailPage/>,
                    action:deleteFabricAction,
                 },
                 {
                    path:'edit',
                    element:<EditFabricPage/>,
                    action:manipulateFabricAction,
                 }
            ],
         },
         {
            path:'new',
            element:<NewFabricPage/>,
            action:manipulateFabricAction
         },
         ]
         },
        {
            path:'cart',
            element:<CartPage/>
        },
        {
            path:'auth',
            element:<Authentication/>
        },
    ],
    },
]);

function App() {
  return (
  <RouterProvider router={router}/>
  );
}

export default App;
