import {useState, useEffect, useContext} from 'react';
import { Tabs, Tab } from '../components/Tabs';
import classes from './clientorders.module.css';
import {AuthContext} from '../Context/auth-context';

const ClientOrders = () => {
const [activeOrders,setActiveOrders]=useState([]);
const [cancelledOrders,setCancelledOrders]=useState([]);
const [allOrders,setAllOrders]=useState([]);
const {userId, token, isAdmin}=useContext(AuthContext);
    useEffect(()=>{
      const fetchOrders=async()=>{
      console.log(userId);

        if(userId){
          try{
          let url=null;
          console.log("admin",isAdmin);
          if(isAdmin){
                url=`http://localhost:5000/vendor/adminOrders/${userId}`;
                const response=await fetch(url,{
                headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                },});
                console.log(response);
                if(!response)
                    throw new Error('Could not fetch your orders');
                const ordersResp=await response.json();
                console.log(ordersResp);
                const orders=ordersResp.data;
                console.log(orders);
                if(orders.length){
                    console.log(orders[0].item_id);
                    setAllOrders(orders);
                    setActiveOrders(orders.filter((order)=>order.status==='created'));
                    setCancelledOrders(orders.filter((order)=>order.status==='cancelled'));
                    console.log(orders.length);
                    console.log(activeOrders.length);
                }
            }
            else{
                url=`http://localhost:5000/orders/clientOrders/${userId}`;
                const response=await fetch(url,{
                headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                },});
                console.log(response);
                if(!response)
                    throw new Error('Could not fetch your orders');
                const ordersResp=await response.json();
                console.log(ordersResp);
                const orders=ordersResp.data;
                console.log(orders);
                if(orders.length){
                    console.log(orders[0].items[0]);
                    setAllOrders(orders);
                    setActiveOrders(orders.filter((order)=>order.status==='created'));
                    setCancelledOrders(orders.filter((order)=>order.status==='cancelled'));

                }
            }
          }catch(err){
            console.log(err.message);
          }
        }
      }
      fetchOrders();
    },[userId]);

      const getItemDetails=async(item_id)=>{
        console.log(item_id);
        const itemResp=await fetch(`http://localhost:5000/store/${item_id}`);
        const {data}=await itemResp.json();
        //console.log(product);
        console.log(data.sub_category);
        return data.type;
    }

    return (
        <div className={classes.container}>
          <h1>My Orders</h1>

          {!isAdmin?(<Tabs>
            <Tab label="Recent Orders">
              <h2>Recent Orders</h2>
              {activeOrders.length > 0 ? (
                activeOrders.map(order => (
                  <div key={order.id} className={classes.order}>
                    <div  className={classes.order_summary}>
                        <span style={{width:'200px'}}>Order ID: {order._id}</span>
                        <span>Status: {order.status}</span>
                        <span>Amount: {order.totalAmount}</span>
                        <span>Items: {order.items.length}
                        {order.items.map(item => (
                           <div key={item.id} className={classes.item}>
                           {item.type}</div>))}
                       </span>
                    </div>
                  </div>
                ))
              ) : (
                <p>No active orders found.</p>
              )}
            </Tab>
            <Tab label="Cancelled Orders">
              <h2>Cancelled Orders</h2>
              {cancelledOrders.length > 0 ? (
                cancelledOrders.map(order => (
                  <div key={order.id} className={classes.order}>
                    <p>Order ID: {order.id}</p>
                    <p>Status: {order.status}</p>
                    <p>Amount: {order.amount}</p>
                  </div>
                ))
              ) : (
                <p>No cancelled orders found.</p>
              )}
            </Tab>
            <Tab label="All Orders">
              <h2>All Orders</h2>
              {allOrders.length > 0 ? (
                allOrders.map(order => (
                  <div key={order.id} className={classes.order}>
                    <p>Order ID: {order.id}</p>
                    <p>Status: {order.status}</p>
                    <p>Amount: {order.amount}</p>
                    {/* Add other order details as needed */}
                  </div>
                ))
              ) : (
                <p>No orders found.</p>
              )}
            </Tab></Tabs>
            ):
            (<Tabs>
            <Tab label="Recent Orders">
              <h2>Recent Orders</h2>
              {activeOrders.length? (
                activeOrders.map(order => (
                  <div key={order.order_id} className={classes.order}>
                    <div  className={classes.order_summary}>
                        <span style={{width:'200px'}}>Order ID: {order.order_id}</span>
                        <span>Status: {order.status}</span>
                         <span>Items:
                            <div key={order.item_id} className={classes.item}>
                            {order.item_details.type}</div>
                        </span>
                        <span>Quantity: {order.quantity}</span>
            )
                    </div>
                  </div>
                ))
              ) : (
                <p>No active orders found.</p>
              )}
            </Tab>
            <Tab label="Cancelled Orders">
              <h2>Cancelled Orders</h2>
              {cancelledOrders.length? (
                cancelledOrders.map(order => (
                  <div key={order.id} className={classes.order}>
                    <p>Order ID: {order.id}</p>
                    <p>Status: {order.status}</p>
                    <p>Amount: {order.amount}</p>
                    {/* Add other order details as needed */}
                  </div>
                ))
              ) : (
                <p>No cancelled orders found.</p>
              )}
            </Tab>
            <Tab label="All Orders">
              <h2>All Orders</h2>
              {allOrders.length ? (
                allOrders.map(order => (
                  <div key={order.id} className={classes.order}>
                    <p>Order ID: {order.id}</p>
                    <p>Status: {order.status}</p>
                    <p>Amount: {order.amount}</p>
                    {/* Add other order details as needed */}
                  </div>
                ))
              ) : (
                <p>No orders found.</p>
              )}
            </Tab></Tabs>
            )
    }

        </div>
      );
    }

export default ClientOrders;
