import {useState,useEffect,useContext,useCallback} from 'react';
import {useDispatch} from 'react-redux';
import {useNavigate, useParams} from 'react-router-dom';
import {cartActions} from '../Store/cart_slice';
import PaymentForm from '../components/PaymentForm';
import Card from '../components/card';
import {OrderContext} from '../Context/order-context';


const OrderPage=()=>{

    const {orderId}=useParams();
    console.log(orderId);
    const dispatch=useDispatch();
    const {status,item, totalQuantity, totalAmount, updateStatus}=useContext(OrderContext);
    console.log(item, totalQuantity, totalAmount);
    /*let display_items=[];
    display_items=item;
    console.log(display_items);*/
    const navigate=useNavigate();
    //const cart=useSelector((state)=>state.cart);
    const [razorpayKey,setKey]=useState(null);
    const [order,setOrder]=useState(null);
useEffect(()=>{
        if(!status || status!=='created')
            navigate('/items');
        console.log(razorpayKey);
        if(razorpayKey) return;
    const checkOut=async()=>{
        try{

console.log("in checkout");
            const responseKey=await fetch('http://localhost:5000/getkey');
            if(!responseKey.ok)
                throw new Error("Payment failed");
            const {key}=await responseKey.json();
            setKey(key);
            const response=await fetch('http://localhost:5000/payment/checkout',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({amount:totalAmount})
                            });
            if(!response.ok)
                throw new Error("Payment failed");
            const {created_order}=await response.json();
            //let {order}=data;

            if(created_order) setOrder(created_order);
            console.log(order);
            }catch(err){
                updateStatus('failed');
                console.log(err.message);
                navigate('/items');
            }

    }
    checkOut();
},[]);
    const paymentHandler=async(formData)=>{
    console.log("in ph");
    var options = {
          key: razorpayKey,
          amount: order.amount,
          currency: "INR",
          name: "J'Adore",
          description: "Test Transaction",
          image: "https://example.com/your_logo",
          order_id: order.id,
          handler: async(response)=>{
          const data=null;
            try{
                const responseData=await fetch('http://localhost:5000/payment/paymentverification',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify(response)
                });
                let {data}=await responseData.json();

                if(data){
    //update rzr_payment_id in Orders
console.log(formData);
                 const payment_response=await fetch('http://localhost:5000/orders',{
                            method:'PUT',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({id:orderId,order_id:order.id,rzr_payment_id:data,customer_data:formData,status:'confirmed'})
                });

                if(payment_response){
    //delete from Cart
                //const cartIdString = localStorage.getItem('cartId');
                //const cart_id = cartIdString ? JSON.parse(cartIdString) : null;
                const cart_id=JSON.parse(localStorage.getItem('cartId'))
                console.log(cart_id);
                if(cart_id){
                    const response=await fetch(
                            `http://localhost:5000/cart/${cart_id}`,
                            { method:'DELETE'}
                          );
                      console.log(response);
                      if(!response)
                      localStorage.removeItem('cartId');
                      dispatch(cartActions.emptyCart());

                }
                updateStatus('confirmed');
                console.log(order.id,data);
                navigate('/paymentsuccess/'+order.id+'/'+data);
                }
               }
            }catch(err){
                console.log(err);
                updateStatus('failed');

            }

          },
          prefill: {
              "name": formData.name,
              "email": formData.email,
              "contact": formData.phone
          },
          notes: {
              "address": "J'Adore Corporate Office"
          },
          theme: {
              "color": "#8F0555"
          }
    };
     var rzp1 = new window.Razorpay(options);
     rzp1.open();
 };
return(
    <>
    {order !== null && razorpayKey !== null  && (
    <div style={{ display: 'flex',width:'100%' }}>
        <PaymentForm onSubmit={paymentHandler}/>
        {item.length!==0 &&
        <Card style={{ display: 'flex', flexDirection: 'column' }} >
        {item.map((itm)=>(
        <>
            <div style={{ display: 'flex', alignItems: 'bottom' }}>
                <span><img src={itm.image} style={{ width: '50px', height: '50px' }}  alt="" /></span>
                <span>{itm.type}</span>
                <span>Rs. {itm.rate?.toFixed(2)}</span>
                <span>X {itm.quantity}</span>
                <span>Rs. {Number(itm.quantity*itm.rate)?.toFixed(2)}</span>
            </div></>
            ))}
            <div style={{marginTop:'auto', textAlign:'right'}}><span></span>
                <span>No. of items: {totalQuantity}</span>
                <span></span>
                <span>Amount: Rs {totalAmount?.toFixed(2)}</span>
            </div>
        </Card>}
    </div>
    )}
    </>
    );
};
export default OrderPage;
