import { Suspense} from 'react';
import {uiActions } from '../Store/ui_slice';
import {useParams,useLoaderData,json,defer,Await} from 'react-router-dom';
import FabricsList from '../components/FabricsList';

function FabricsPage() {

  const [isLoading, setIsLoading] = useState(false);
  const [Fabrics, setFabrics] = useState();


  useEffect(() => {

    async function fetchFabrics(dispatch){
        try{
              const response = await fetch('http://localhost:5000/fabrics');

              if (!response.ok) {
                throw new Error('Fetching Fabrics failed')
              }
              const resData = await response.json();
              console.log(resData);
              setFabrics(resData);
          }catch(err){
              dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: error.message,
                })
            );
           }
        }
        fetchFabrics();
    },[]);
    return <FabricsList list={fabrics}/>
  }     

    export default FabricsPage;
