//import { v4 as uuidv4 } from 'uuid';
import axios from "axios";
import {useState,useEffect,useRef,useContext} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {useNavigate, useParams} from 'react-router-dom';
//import classes from './FabricItemOrder.module.css';
import {cartActions} from '../Store/cart_slice';
import {uiActions} from '../Store/ui_slice';
import PaymentForm from '../components/PaymentForm';
import Card from '../components/card';
import {OrderContext} from '../Context/order-context';


const OrderPage=()=>{


    const dispatch=useDispatch();
    const orderCtx=useContext(OrderContext);
    const navigate=useNavigate();
    const cart=useSelector((state)=>state.cart);
    const [razorpayKey,setKey]=useState(null);
    const [order,setOrder]=useState(null);

useEffect(()=>{

    const checkOut=async()=>{
        try{
            const responseKey=await fetch('http://localhost:5000/getkey');
            if(!responseKey.ok)
                throw new Error("Payment failed");
            const {key}=await responseKey.json();
            console.log(orderCtx.totalAmount);
            setKey(key);
            const response=await fetch('http://localhost:5000/payment/checkout',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({amount:orderCtx.totalAmount})
                            });
            if(!response.ok)
                throw new Error("Payment failed");
            const {order}=await response.json();
            //let {order}=data;
            console.log(order);
            setOrder(order);
            }catch(err){
               //console.log(err.message);
               navigate('/fabrics');
            }

    }
    checkOut();
},[]);
    const paymentHandler=async(formData)=>{
    var options = {
          key: razorpayKey,
          amount: order.amount,
          currency: "INR",
          name: "J'Adore",
          description: "Test Transaction",
          image: "https://example.com/your_logo",
          order_id: order.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
          handler: async(response)=>{
            try{
                const responseData=await fetch('http://localhost:5000/payment/paymentverification',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify(response)
                });
                const {data}=await responseData.json();

    //update 
                console.log(data);
               navigate('/paymentsuccess/'+data);

            }catch(err){
                console.log(err);
            }
          },
          prefill: {
              "name": formData.first_name+" "+formData.last_name,
              "email": formData.email,
              "contact": formData.phone
          },
          notes: {
              "address": "J'Adore Corporate Office"
          },
          theme: {
              "color": "#8F0555"
          }
    };
     var rzp1 = new window.Razorpay(options);
     rzp1.open();
 };
return(
    <>
    {order !== null && razorpayKey !== null  && (
    <div style={{ display: 'flex' }}>
        <PaymentForm onSubmit={paymentHandler}/>
        <Card >
            <div style={{ display: 'flex'}}>
            <img src={orderCtx.item.image} style={{ width: '100px', height: '100px' }}  alt="" />
            <span>{orderCtx.item.type}
                <div >
                    <span>Rs. {orderCtx.item.rate}</span>
                    <span>X {orderCtx.totalQuantity}</span>
                </div>
            </span>
        <span>Amount<div>Rs {orderCtx.totalAmount}.00</div></span>
            </div>
        </Card>
    </div>
    )}
    </>
    );
};
export default OrderPage;
