import { useParams, Link } from "react-router-dom";
import {OrderContext} from '../Context/order-context';
import {useContext, useSate} from 'react';
import Card from '../components/card';
const PaymentSuccess = () => {
    const {reference} = useParams();
    const [orderData,setOrderData]=useSate({item:[],totalQuantity:0,totalAmount:0});
    const {item,totalQuantity,totalAmount,clearContext}=useContext(OrderContext);
    setOrderData([item],totalQuantity,totalAmount);
    clearContext();
    return (
    <>
    <div style={{ textAlign:'center',display: 'inline-block',width:'600px' }}>

        <div><h2>Order successfully placed !</h2></div>
        <div style={{ textAlign:'center'}}><Link to="/items">Back</Link></div>

    </div>
    <Card >
    <div style={{textAlign:'right',fontSize: '14px'}}>
        <div><span>Date of purchase: </span><span>{new Date().toLocaleDateString('en-IN')}</span></div>
        <div><span>Order Id:</span><span>{reference}</span></div>
    </div>
            {orderData.item.map((itm)=>(

        <div key={itm.id} style={{ display: 'flex', alignItems: 'center' }}>
        <img src={itm.image} style={{ width: '100px', height: '100px' }}  alt="" />
        <span>{itm.type}
            <div>Rs. {itm.rate?.toFixed(2)} /meter</div>
        </span>
        <span><div>{itm.quantity} pcs</div></span>
        <span><div>Rs. {(itm.quantity*itm.rate)?.toFixed(2)} </div></span>
        </div>

        ))}
        <hr style={{ borderTop: '1px solid #ccc', margin: '20px 0' }} />
         <div style={{ textAlign: 'right', fontSize: '14px' }}>
        <span>No. of items: {orderData.totalQuantity}</span>
        <span></span><span></span>
        <span>Amount: Rs. {orderData.totalAmount?.toFixed(2)}</span>
        </div>
    </Card>
    <h3>Check out other collections</h3>
    <span>Silk Fabrics</span><span>Mul cotton</span>
    </>
    );
}
export default PaymentSuccess;
