            <div style={{minWidth:'700px',marginLeft:'50px',textAlign:'left'}}>{ticket.subject}</div>
            <div style={{minWidth:'100px'}}>{ticket.status}</div>
            <div style={{minWidth:'100px'}}>From {ticket.message[ticket.message.length-1].sender}</div>
        <div><i onClick={removeEntryHandler} class="fa-regular fa-trash-can"></i></div>

        </NavLink>
      </li>))}
