import classes from './FabricItem.module.css';
import FabricItemOrder from './FabricItemOrder';
import FabricForm from './FabricForm';

import {useContext} from 'react';
import {AuthContext} from '../Context/auth-context';

function FabricItem(props){
const authCtx=useContext(AuthContext);
const item=props.item;
console.log( authCtx.userId,item.created_by);
//const name=item.type+" "+item.sub_category+" "+item.category
return(
<>
    <div className={classes.disp}>
        <div>
            {item.category && item.category.toLowerCase()==='fabrics' && <div className={classes.img_container}><img style={{height:'400px',width:'400px'}} src={item.image}/></div>}
            {item.category && item.category.toLowerCase()!=='fabrics' && <div className={classes.img_container}><img style={{height:'350px',width:'250px'}} src={item.image}/></div>}
            <h3>{item.desc}</h3>
        </div>

        {!authCtx.isAdmin && <div><FabricItemOrder item={{id:item._id,category:item.category,type:item.type,rate:item.price,image:item.image,desc:item.desc,created_by:item.created_by}}/></div>}

        {authCtx.isAdmin && authCtx.userId===item.created_by && <div><FabricForm mode='edit' item={{id:item._id,category:item.category,sub_category:item.sub_category,type:item.type,rate:item.price,image:item.image,desc:item.desc}}/></div>}

    </div>
}
</>
);
}
export default FabricItem;
