import {useState} from 'react';

const SubscriptionForm=()=>{
const [isEmailSub,setEmailSub]=useState(false);
const [isSmsSub, setSmsSub] = useState(false);
const [isPushSub, setPushSub] = useState(false);
const [phoneNumber, setPhoneNumber] = useState('');
const handleSubmit=(e)=>{
    e.preventDefault();
    console.log({
          emailSubscribed: isEmailSub,
          smsSubscribed: isSmsSub,
          pushSubscribed: isPushSub,
          phoneNumber: isSmsSub ? phoneNumber : null,
        });
}
return(
<form onSubmit={handleSubmit}>
  <h3>Subscribe to Notifications</h3>

  {/* Email Notifications */}
  <label>
    <input  type="checkbox"
             checked={isEmailSub}
             onChange={() => setEmailSub(!isEmailSub)}
    Email Notifications/>
  </label>

  {/* SMS Notifications */}
  <label>
    <input  type="checkbox"
             checked={isSmsSub}
             onChange={() => setSmsSub(!isSmsSub)}
    SMS Notifications/>
  </label>


  {isSmsSub && (
          <input
            type="tel"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            placeholder="Enter your phone number"
          />
        )}

  {/* Push Notifications */}
  <label>
    <input type="checkbox" name="pushNotifications" />
    Browser Push Notifications
  </label>
  </form>
  )}

  export default SubscriptionForm;
