  return (
  <>
  <div className="nav-bar">
    <ul className="nav-links">
      <li>
            <NavLink to="/items" ><h1>J'Adore</h1></NavLink>
      </li>
       <li>
            {!authCtx.isLoggedIn &&   (<NavLink to={`/user?mode=login`} className={({isActive})=>isActive?classes.active:undefined}end>Login/Sign up</NavLink>)}
       </li>
       <li>
            {authCtx.isLoggedIn &&  (<button onClick={authCtx.logout} >Logout</button>)}
       </li>
       <li>
               <NavLink to="/" className={({isActive})=>isActive?classes.active:undefined}end>Wishlist</NavLink>
       </li>
       <li>
               <NavLink className={({isActive})=>isActive?classes.active:undefined}end>Cart<CartButton/></NavLink>
      </li>
    </ul>
    </div>
    </>
  );
}

export default MainNavigation;
