import { useParams } from "react-router-dom";
import {OrderContext} from '../Context/order-context';
import {useContext} from 'react';
import Card from '../components/card';
const PaymentSuccess = () => {

    const {reference} = useParams();
    const {item,totalQuantity,totalAmount}=useContext(OrderContext);

    return (
    <>
    <h2>Order successfully placed !</h2>
    <Card >
    <div style={{align:'right'}}>
        <span>Date of purchase</span><span>{new Date().toLocaleDateString('en-IN')}</span>
        <span>Order Id:</span><span>{reference}</span>
    </div>
            {item.map((itm)=>(

        <div style={{ display: 'flex'}}>
        <img src={itm.image} style={{ width: '100px', height: '100px' }}  alt="" />
        <span>{itm.type}
            <div>Rs. {itm.rate?.toFixed(2)} /meter</div>
        </span>
        <span><div>{itm.quantity} pcs</div></span>
        <span><div>Rs. {(itm.quantity*itm.rate)?.toFixed(2)} </div></span>
        </div>

        ))}
        <div style={{marginTop:'auto', textAlign:'right'}}>
        <span></span>
        <span>No. of items: {totalQuantity}</span>
        <span></span><span></span>
        <span>Amount: Rs. {totalAmount?.toFixed(2)}</span>
        </div>
    </Card>
    <h3>Check out other collections</h3>
    <span>Silk Fabrics</span><span>Mul cotton</span>
    </>
    );
}
export default PaymentSuccess;
