import { createSlice } from '@reduxjs/toolkit';

const cartSlice=createSlice({
    name:'cart',
    initialState:{ items:[{id:'0',type:'',rate:'',quantity:'',totalPrice:''}],totalQuantity:0,changed:false },
    reducers:{

        addToCartFromList(state,action){
            const newItem=action.payload.item;
            const quantity=action.payload.quantity;

            state.items.push({
                id:newItem.Fabid,
                quantity:quantity,
                rate:newItem.price,
                totalPrice:newItem.price*quantity,
                type:newItem.type,
            });
            state.totalQuantity=state.totalQuantity+1;
            state.changed=true;


        },

        replaceCart(state,action){
            state.totalQuantity=action.payload.totalQuantity;
            state.items=action.payload.items;
        },

        addToCart(state,action){
            const newItem=action.payload;
    console.log(state.cart.items[0].quantity);
            const existingItem=state.cart.items.find((item)=>item.id===newItem.id);
            state.cart.totalQuantity++;
            state.cart.changed=true;

            if(!existingItem){
               state.cart.items.push({
                id:newItem.id,
                quantity:1,
                rate:newItem.rate,
                totalPrice:newItem.rate,
                type:newItem.type,
            })
            }else{
                existingItem.quantity++;
                existingItem.totalPrice=existingItem.totalPrice+newItem.rate;
            }
        },
        removeFromCart(state,action){
       
            const id=action.payload;
            const existingItem=state.items.find((item)=>item.id === id);
            if(existingItem.quantity===1){
                state.items=state.items.filter((item)=>item.id !== id);
                state.totalQuantity--;
            }else{
               existingItem.quantity--;
               existingItem.totalPrice=existingItem.totalPrice-existingItem.rate;
            }
            state.changed=true;
        },
    }
});


export const cartActions=cartSlice.actions;
export default cartSlice;
