import { useState, useContext } from 'react';
import { AuthContext } from '../Context/auth-context';

const SubscriptionForm = () => {
  const [isEmailSub, setEmailSub] = useState(false);
  const [isSmsSub, setSmsSub] = useState(false);
  const [isPushSub, setPushSub] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');
  const {token}=useContext(AuthContext);
  const [saved,setSaved]=useState(false);

  const handleSubmit =async (e) => {
      e.preventDefault();
      const notifications={
      emailSubscribed: isEmailSub,
      smsSubscribed: isSmsSub,
      pushSubscribed: isPushSub,
      phoneNumber: isSmsSub ? phoneNumber : null,
    };
    console.log(notifications);
    const response=await fetch(`http://localhost:5000/user/notifications`,
          {
              method: 'PUT',
              headers:{'content-type':'application/json', 'Authorization':'Bearer '+ token},
              body:JSON.stringify({
                notifications:notifications})
          });
      console.log(response);
      setSaved(false);
  };

    const handleCancel=() =>{

    }


  return (
    <form onSubmit={handleSubmit} style={{ width: '100%', textAlign: 'left', border:'2px solid pink' }}>
      <h3 style={{textAlign:'center',marginBottom:'40px',backgroundColor:'pink'}}>Subscribe to Notifications</h3>
    <div style={{display:'flex', justifyContent:'top'}}>
      {/* Email Notifications */}
      <div style={{justifyContent:'top',display:'flex',width:'500px'}}>
        <input style={{width:'50px'}}
          type="checkbox"
          checked={isEmailSub}
          onChange={() => setEmailSub(!isEmailSub)}
          id="emailSub"
        />
        <label htmlFor="emailSub" style={{ marginLeft: '10px', justifyContent:'top' }}>Email Notifications</label>
      </div>

      {/* SMS Notifications */}
      <div style={{justifyContent:'top',display:'flex',width:'500px'}}>
        <input  style={{width:'50px'}}
          type="checkbox"
          checked={isSmsSub}
          onChange={() => setSmsSub(!isSmsSub)}
          id="smsSub"
        />
        <label htmlFor="smsSub" style={{ marginLeft: '10px' }}>SMS Notifications</label>
      </div>

        {/* Push Notifications */}
      <div style={{justifyContent:'top',display:'flex',width:'500px'}}>
        <input style={{width:'50px'}}
          type="checkbox"
          checked={isPushSub}
          onChange={() => {setSaved(true); setPushSub(!isPushSub)}}
          id="pushSub"
        />
        <label htmlFor="pushSub" style={{ marginLeft: '10px' }}>Browser Push Notifications</label>
      </div>      </div>

      {/* Phone Number Input */}
        {isSmsSub && (
            <div style={{ marginLeft: '500px', marginBottom: '10px' }}>
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="Enter your phone number"
                  style={{ width:'200px'}}
                />
          </div>
            )}




      <div style={{textAlign:'center',fontStyle:'italic'}}><p>(To unsubscribe clear the checkbox)</p></div>
       <div style={{textAlign:'center',marginTop:'20px'}} >
        <button type="submit" style={{ marginTop: '20px',padding: '10px 10px',backgroundColor: '#e44da5',
                                       color: '#fff',border: 'none',borderRadius: '5px',cursor: 'pointer',
                                       cursor: (!saved) ? 'not-allowed' : 'pointer', // Disable cursor if either condition is true
                                       opacity: (!saved) ? 0.5 : 1,
                                       }} disabled={!saved}>OK</button>

        <button type="button" style={{ marginTop: '20px',padding: '10px 10px',backgroundColor: '#e44da5',
                                      color: '#fff',border: 'none',borderRadius: '5px',cursor: 'pointer'}}>Cancel</button>
       </div>

    </form>
  );
};

export default SubscriptionForm;
