import classes from './FabricItem.module.css';
import FabricItemOrder from './FabricItemOrder';
import FabricForm from './FabricForm';

import {useContext} from 'react';
import {AuthContext} from '../Context/auth-context';

function FabricItem(props){
const authCtx=useContext(AuthContext);
const item=props.item;
console.log(item);
const name=item.type+" "+item.sub_category+" "+item.category
return(
<>
    <div className={classes.disp}>
        <div>
            <img style={{height:'250px',width:'250px'}} src={item.image}/>

            <h3>{item.desc}</h3>
        </div>

        {!authCtx.isAdmin && <div><FabricItemOrder item={{id:item._id,category:item.category,type:item.type,rate:item.price,image:item.image,desc:item.desc}}/></div>}

        {authCtx.isAdmin && <div><FabricForm mode='edit' item={{id:item._id,category:item.category,sub_category:item.sub_category,type:item.type,rate:item.price,image:item.image,desc:item.desc}}/></div>}

    </div>
</>
);
}
export default FabricItem;
