import classes from './FabricsList.module.css';
import {NavLink} from 'react-router-dom';
function FabricsList({ events }) {
const items= Object.keys(events).map(id => {
  return { uuid: id,id:events[id].id, name:events[id].name, desc:events[id].desc ,image:events[id].image};
});
  return (
  <ul>
    <h2>Fabric Store</h2>

      {items.map((item) => (
    <div class={classes.responsive}>
      <div class={classes.gallery}>
        <NavLink to={{pathname:`/fabrics/${item.id}` }} state={{itemName:`${item.name}`, itemDesc:`${item.image}`}} className={({isActive})=>isActive?classes.active:undefined}end>
            <img src={item.image} alt="Cinque Terre" width="300" height="200"/>
        </NavLink>
        <div class={classes.desc}>{item.name} {item.desc}</div>

      </div>
    </div>

  ))
}
</ul>
);
}
export default FabricsList;
