import { createSlice } from '@reduxjs/toolkit';

const cartSlice=createSlice({
    name:'cart',
    initialState:{ items:[],totalQuantity:0,totalAmount:0,changed:false,isInitial:true },
    reducers:{



        replaceCart(state,action){
            state.totalQuantity=action.payload.totalQuantity;
            state.totalAmount=action.payload.totalAmount;
            state.items=action.payload.items;
            state.isInitial=false;
        },

        addToCart(state,action){
            const newItem=action.payload.item;
            console.log(newItem);
            const quantity=Number(newItem.quantity||action.payload.quantity);
            const existingItem=state.items.find((item)=>item.id===newItem.id);

            if(!existingItem){
               state.items.push({
                id:newItem.id,
                quantity:quantity,
                rate:newItem.rate,
                totalPrice:newItem.rate*quantity,
                type:newItem.type,
                image:newItem.image,
            })
                state.totalQuantity++;
            }else{
                existingItem.quantity=quantity;
                existingItem.totalPrice=quantity*newItem.rate;
            }
            state.totalAmount+=Number(quantity)*Number(newItem.rate);
            console.log(state.items, state.totalAmount);
            state.changed=true;
        },


        removeFromCart(state,action){
            const id=action.payload.id;
            console.log(state.totalQuantity);
            state.items=state.items.filter((item)=>item.id !== id);
            state.totalQuantity--;
            state.changed=true;
        },

        changeCartItem(state,action){
            const id=action.payload.id;
            const qty=action.payload.quantity;
            if(qty===0){
                state.items=state.items.filter((item)=>item.id !== id);
                state.totalQuantity--;
            }else{
                const existingItem=state.items.find((item)=>item.id===id);
                existingItem.quantity=Number(qty);
                existingItem.totalPrice=Number(qty)*(existingItem.rate);
            }
            state.changed=true;
        }
    }
});


export const cartActions=cartSlice.actions;
export default cartSlice;
