import classes from './mainmenu.module.css';
import {NavLink} from 'react-router-dom';

function MainMenu( props ) {
const {items}=props;

  return (
    <div className={classes.events}>

      <ul className={classes.list}>
        {items.map((item) => (
          <li key={item.value} className={classes.item}>
           <div className={classes.itemContainer}>
<NavLink to={{pathname:`/store/${item.value}`}} className={({isActive})=>isActive?classes.active:undefined}end>
  {item.value==='fabrics' &&  <img style={{height:'250',width:'200px'}} className={classes.itemImage} src="https://i.pinimg.com/236x/a4/b6/ad/a4b6ad2e598e865e1f4dac896292769c.jpg"/>}
  {item.value==='sarees' &&  <img style={{height:'300px',width:'200px'}} className={classes.itemImage} src="https://i.pinimg.com/236x/5c/f5/3e/5cf53ed9f63d6cbbb7eb65fbd7844687.jpg"/>}
  {item.value==='lehengas' &&  <img style={{height:'300px',width:'200px'}} className={classes.itemImage} src="https://i.pinimg.com/236x/bb/37/9b/bb379b539c91dc4b4fc8e1f85a775463.jpg"/>}
  {item.value==='salwars' &&  <img style={{height:'250',width:'200px'}} className={classes.itemImage} src="https://i.pinimg.com/474x/10/3d/8d/103d8dffba668b24a1c5de10d09572c7.jpg"/>}
  {item.value==='kurtas' &&  <img style={{height:'300px',width:'200px'}} className={classes.itemImage} src="https://i.pinimg.com/236x/3a/0e/60/3a0e60aa48ce1b8f53a8f84c735b798d.jpg"/>}
   <h2 className={classes.itemTitle}>{item.label}</h2>
            </NavLink>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MainMenu;

