import {useState,useEffect,useRef,useContext} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {useNavigate} from 'react-router-dom';
import classes from './FabricItemOrder.module.css';
import {cartActions} from '../Store/cart_slice';
import {uiActions} from '../Store/ui_slice';
import { sendCartData, fetchCartData } from '../Store/cart_actions';
import CartPage from './Cart';
import Modal from './Modal';
import {OrderContext} from '../Context/order-context';

const FabricItemOrder=(props)=>{
    const [Quantity,setQuantity]=useState(0);
    const navigate=useNavigate();
    const orderContext=useContext(OrderContext);
    const {id,type,rate,image}=props.item;
    const selectedItem={id,type,rate,image};
    const dispatch=useDispatch();
    const cart=useSelector((state)=>state.cart);
    const showCart=useSelector((state)=>state.ui.cartIsVisible);
    dispatch(uiActions.clearNotification());

const modal=useRef();

    const handleChange=(e)=>{
        e.preventDefault();
        setQuantity(e.target.value);
    };

    const addToCart=(e)=>{
        e.preventDefault();
        if(cart.isInitial)
             dispatch(fetchCartData());
        dispatch(cartActions.addToCart({item:{
                                                id,
                                                type,
                                                rate,
                                                image,
                                                quantity:Quantity}}));
        dispatch(uiActions.setCartVisibility(true));
        navigate('/Cart');
    };

    const checkoutHandler=async(e)=>{
    e.preventDefault();
    const order_item=selectedItem;
    let order_items=[];
    order_items.push(order_item);
    const order_quantity=Quantity;
    const order_amount=order_item.rate * order_quantity;
    try{
        const response = await fetch('http://localhost:5000/orders',{
                        method:'POST',
                        headers:{'content-type':'application/json'},
                        body:JSON.stringify({items:order_items,totalQuantity:order_quantity,totalAmount:order_amount})
        });
        const {orderId} = await response.json();
        console.log(orderId);


    /*orderContext.item=order_item;
    orderContext.totalQuantity=order_quantity;
    orderContext.totalAmount=order_item.rate*order_quantity;*/
    setOrderContext({item:order_items, totalQuantity:order_quantity, totalAmount:order_amount});
    console.log(orderContext.item,orderContext.totalQuantity,orderContext.totalAmount,orderId);
    navigate('/order/'+orderId);

    }
    catch(error){
        console.log(error.message);
        dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: 'Could not place Order. Please try again',
              })
            );
        }
  };
return(
<>

<section>
    <form>
    <div className={classes.disp}>
    <div><span>Price:</span><span>{rate} </span><span>Rs/meter</span></div>
    <div><span> Quantity</span><span><input onChange={handleChange} name="qty" type="text"  /></span><span>meter</span></div>
     <div><span>Minimum Order 1 meter</span></div>
    <div  className={classes.disp}>
        <span><button onClick={addToCart}>ADD TO CART</button></span>
        <span><button onClick={checkoutHandler}>BUY IT NOW</button></span>
    </div>
    </div>
    </form>
    </section>
</>
);
}
export default FabricItemOrder;
