import {useEffect, useRef, useContext} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import classes from './Cart.module.css';

import Card from './card';
import {useNavigate,NavLink} from 'react-router-dom';
import Modal from './Modal';
import Notification from './notification';

import {WishlistContext} from '../Context/wishlist-context';

const WishlistPage=()=>{

    const {wishlist}=useContext(WishlistContext);
console.log(wishlist);
    //const showCart = useSelector((state) => state.ui.cartIsVisible);
    const notification = useSelector((state) => state.ui.notification);
    const modal=useRef();
    const navigate=useNavigate();

     useEffect(()=>{
         if(wishlist.length>0)
            modal.current.open();
         else
            navigate('/');
     }, []);

    function handleReset(){
        modal.current.close();
        navigate('/');
     }

       return(<>
        <Modal ref={modal} onReset={handleReset} >
        <h2>My Wishlist</h2>

        <div className={classes.cartItemsContainer}>
        <ul>
        { wishlist.length && wishlist.map((item)=>(
            <li key={item._id} >
                <div className={classes.item}>
                <NavLink to={{pathname:`/fabrics/${item._id}/${item.category}` }}  className={({isActive})=>isActive?classes.active:undefined}end>
                {item.category.toLowerCase()==='fabrics' && <img style={{width:'100px',height:'100px'}} src={item.image} alt="Cinque Terre"/>}
                {item.category.toLowerCase()!=='fabrics' && <img style={{width:'100px',height:'100px'}} src={item.image} alt="Cinque Terre"/>}
                <div className={classes.desc}>{item.desc}</div></NavLink>
                </div>
            </li>))}
        </ul>
        </div>

        <div className={classes.actions} >
        <button className={classes.button} onClick={handleReset}>OK</button>
        </div>
        </Modal>

</>
);

 }


    export default WishlistPage;
