import {useState, useEffect, useCallback} from 'react';
let logoutTimer;
export const useAuth=()=>{
    const [token, setToken]= useState(null);
    const [tokenExpirationDate, setTokenExpirationDate]= useState(null);
    const [userId, setUserId]= useState(null);
    const [isLoggedIn, setIsLoggedIn]= useState(false);
    const [isAdmin, setIsAdmin]= useState(false);
    const [superAdmin, setSuperAdmin]= useState(null);

    const login=useCallback((uid, token, expirationDate,userType, super_admin)=> {
    console.log(uid, expirationDate, userType, typeof(super_admin),super_admin);
      setToken(token);
      setUserId(uid);
      setIsLoggedIn(true);
      setIsAdmin(userType==='admin');
     /* const isSuperAdmin = typeof super_admin === 'boolean'
          ? super_admin
          : super_admin === "true";*/
      if(typeof(super_admin)==='string'){
        const isSuperAdmin=(super_admin==="true");
        setSuperAdmin(isSuperAdmin);
     }
      else{
        console.log(super_admin);
        setSuperAdmin(super_admin);
     }
      console.log(isLoggedIn, isAdmin,superAdmin);
      const tokenExpirationDate= expirationDate || new Date(new Date().getTime() + 1000 * 60 * 60);
      setTokenExpirationDate(tokenExpirationDate);
  console.log("storing in local storage",superAdmin);
      localStorage.setItem(
      'userData',
      JSON.stringify({
      userId: uid,
      token: token,
      expiration: tokenExpirationDate.toISOString(),
      userType: userType,
      superAdmin: superAdmin.toString(),
      })
      );
    },[]);

    const logout=useCallback(()=>{
    console.log("hook logout ");
        setToken(null);
        setIsLoggedIn(false);
        setIsAdmin(false);
        setSuperAdmin(false);
        setTokenExpirationDate(null);
        setUserId(null);
        localStorage.removeItem('userData');
        localStorage.removeItem('cartId');
        localStorage.removeItem('orders');
        localStorage.removeItem('Wishlist');
        localStorage.removeItem('avatar');

    },[]);

    useEffect(()=>{                                                                             //auto logout
        if(token && tokenExpirationDate){
            const remainingTime = tokenExpirationDate.getTime()-new Date().getTime();
            console.log("remaining time ",remainingTime);
            logoutTimer=setTimeout(logout,remainingTime);
        }else{
            clearTimeout(logoutTimer);
        }
    },[token, tokenExpirationDate, logout]);

    useEffect(()=>{
    console.log("auto-login");
    const storedData=JSON.parse(localStorage.getItem('userData'));
    console.log("page refresh");
        if(storedData && storedData.token && new Date(storedData.expiration) > new Date()){
            /*const isSuperAdmin = typeof storedData.superAdmin === 'boolean'
            ? storedData.superAdmin
            : storedData.superAdmin === "true";
            console.log(isSuperAdmin);*/
            login(storedData.userId, storedData.token, new Date(storedData.expiration),storedData.userType,storedData.superAdmin);
        }
    },[login]);



    return {token, login, logout, userId, isLoggedIn, isAdmin,superAdmin};
};
