import { useState } from 'react';

const SubscriptionForm = () => {
  const [isEmailSub, setEmailSub] = useState(false);
  const [isSmsSub, setSmsSub] = useState(false);
  const [isPushSub, setPushSub] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      emailSubscribed: isEmailSub,
      smsSubscribed: isSmsSub,
      pushSubscribed: isPushSub,
      phoneNumber: isSmsSub ? phoneNumber : null,
    });
  };

  return (
    <form onSubmit={handleSubmit} style={{ width: '100%', textAlign: 'left' }}>
      <h3 style={{textAlign:'center'}}>Subscribe to Notifications</h3>

      {/* Email Notifications */}
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'left', marginBottom: '10px' }}>
        <input
          type="checkbox"
          checked={isEmailSub}
          onChange={() => setEmailSub(!isEmailSub)}
          id="emailSub"
        />
        <label htmlFor="emailSub" style={{ marginLeft: '10px' }}>Email Notifications</label>
      </div>

      {/* SMS Notifications */}
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'left', marginBottom: '10px' }}>
        <input
          type="checkbox"
          checked={isSmsSub}
          onChange={() => setSmsSub(!isSmsSub)}
          id="smsSub"
        />
        <label htmlFor="smsSub" style={{ marginLeft: '10px' }}>SMS Notifications</label>
      </div>

      {/* Phone Number Input */}
      {isSmsSub && (
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'left', marginBottom: '10px' }}>
          <label style={{ marginLeft: '10px' }}>
            Enter your Phone Number:
            <input
              type="tel"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              placeholder="Enter your phone number"
              style={{ marginLeft: '10px' }}
            />
          </label>
      </div>
        )}
      {/* Push Notifications */}
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'left', marginBottom: '10px' }}>
        <input
          type="checkbox"
          checked={isPushSub}
          onChange={() => setPushSub(!isPushSub)}
          id="pushSub"
        />
        <label htmlFor="pushSub" style={{ marginLeft: '10px' }}>Browser Push Notifications</label>
      </div>
    </form>
  );
};

export default SubscriptionForm;
