import classes from './FabricsList.module.css';
import {useContext} from 'react';
import {useDispatch} from 'react-redux';
import {uiActions} from '../Store/ui_slice';
import {NavLink, useNavigate} from 'react-router-dom';
import  '@fortawesome/fontawesome-free/css/all.css';
import {AuthContext} from '../Context/auth-context';


function FabricsList({list, category, updateFabricList} ) {
    const dispatch=useDispatch();
    const authCtx=useContext(AuthContext);
    const navigate=useNavigate();

   /* const items= Object.keys(list).map(id => {
      return {id:list[id]._id, name:list[id].type+" "+list[id].sub_category+" "+list[id].category, desc:list[id].desc ,details:list[id].details,image:list[id].image, colour:list[id].colour};
    });*/
console.log(category);
    console.log(list);
    //const [items]=list;

//console.log(category.toLowerCase());
 const removeEntryHandler = (id) => {

    const deleteFabric = async(dispatch)=>{
      try{
           const response=await fetch(
                    `http://localhost:5000/fabrics/${id}`,
                    { method:'DELETE',
                      headers:{Authorization:'Bearer '+ authCtx.token}
                    }
                  );
           if(! response.ok)
                throw new Error("Could not delete the Fabric");
           const resData = await response.json();
           console.log(resData);
           updateFabricList();
           navigate('/store/'+category);
        }
        catch(error){
            console.log(error);
            }
        }
      deleteFabric(dispatch);
   }

   const addFabricHandler = () => {
    navigate('/fabrics/new');
   }



  return (<>
<div>

    {list.length===0 && <h2>We are currently Out of Stock</h2>}
    {list.length!==0 && <h2>{list[0].category} Store</h2>}
</div>
<ul>
  { list.map((item) => (
     <div className={classes.responsive} >
      <div className={classes.gallery}>
     {authCtx.isLoggedIn && authCtx.isAdmin && authCtx.userId===item.created_by && (<>
        <div style={{width:"100%", textAlign:"center"}}><button onClick={addFabricHandler}>Add Item</button></div>
      <li key={item._id}>
        <div className={classes.item}>
        <NavLink to={{pathname:`/fabrics/${item._id}/${item.category}` }}  className={({isActive})=>isActive?classes.active:undefined}end>
            <img style={{width:'250px',height:'250px'}} src={item.image} alt="Cinque Terre"/>
            <div className={classes.desc}>{item.desc}</div>
        </NavLink>
        <div><i class="fa-regular fa-trash-can" onClick={()=>removeEntryHandler(item.id)}></i></div>
        </div>
      </li></>)
    }
    {(!authCtx.isLoggedIn || (authCtx.isLoggedIn && !authCtx.isAdmin)) && (
     <li key={item._id}>
        <div className={classes.item}>
        <NavLink to={{pathname:`/fabrics/${item._id}/${item.category}` }}  className={({isActive})=>isActive?classes.active:undefined}end>
            <img style={{width:'250px',height:'250px'}} src={item.image} alt="Cinque Terre"/>
            <div className={classes.desc}>{item.desc}</div>
        </NavLink>
        </div>
      </li>
      )}
    </div>
   </div>
  ))}

</ul>
</>
);
}
export default FabricsList;
