import { useParams, Link } from "react-router-dom";
import {OrderContext} from '../Context/order-context';
import {useContext, useState, useEffect} from 'react';
import Card from '../components/card';
const PaymentSuccess = () => {
    const {orderId,paymentId} = useParams();
    const [orderData,setOrderData] = useState({item:[],status:'',totalQuantity:0,totalAmount:0});
    const {item,totalQuantity,totalAmount,status,clearContext}=useContext(OrderContext);
    useEffect(() => {
        // Set orderData only once when the component mounts
        if (item.length > 0) {
            setOrderData({ item, status, totalQuantity, totalAmount });
        const transferPayment=async()=>{
        if(paymentId){
        console.log(paymentId);
        try{
             const response=await fetch( "https://api.razorpay.com/v1/payments/"+paymentId+"/transfers",{
             method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": "Basic " + btoa("rzp_test_ishts8E5x2xi0z:1r8duV9yNl4NTRMzQSUZsOHZ")
                },
                body: JSON.stringify({
              "transfers": [
                {
                  "account": "acc_O62uUW1kj15aOt",
                  "amount": 2000,
                  "currency": "INR",
                  "notes": {
                    "name": "adore",
                    "roll_no": "IEC2011025"
                  },
                  "linked_account_notes": [
                    "roll_no"
                  ],
                  "on_hold": true,
                  "on_hold_until": 1671222870
                },
                {
                  "account": "acc_IRQWUleX4BqvYn",
                  "amount": 300,
                  "currency": "INR",
                  "notes": {
                    "name": "Saurav Kumar",
                    "roll_no": "IEC2011026"
                  },
                  "linked_account_notes": [
                    "roll_no"
                  ],
                  "on_hold": false
                }
              ]})
              });
               if (!response.ok) {
                  throw new Error("Request failed");
              }

              const responseData = await response.json();
              console.log(responseData);
            }
            catch(error){
                console.log(error);
            }
          }
          }
          transferPayment();
        }
        return() => {
            clearContext();
         }
    }, [item, status,totalQuantity, totalAmount, clearContext]);

    return (<>
    {orderData.status!=='confirmed' && (<div><h2>Your order could not be placed. Try again</h2></div>)}
    {orderData.status==='confirmed' && (
    <>
    <div style={{ textAlign:'center',display: 'inline-block',width:'600px' }}>
        <div><h2>Order successfully placed !</h2></div>
        <div style={{ textAlign:'center'}}><Link to="/items">Back</Link></div>

    </div>
    <Card >
    <div style={{textAlign:'right',fontSize: '14px'}}>
        <div><span>Date of purchase: </span><span>{new Date().toLocaleDateString('en-IN')}</span></div>
        <div><span>Order Id:</span><span>{orderId}</span></div>
    </div>
            {orderData.item && orderData.item.map((itm)=>(

        <div key={itm.id} style={{ display: 'flex', alignItems: 'center' }}>
        <img src={itm.image} style={{ width: '100px', height: '100px' }}  alt="" />
        <span>{itm.type}
            <div>Rs. {itm.rate?.toFixed(2)} /meter</div>
        </span>
        <span><div>{itm.quantity} pcs</div></span>
        <span><div>Rs. {(itm.quantity*itm.rate)?.toFixed(2)} </div></span>
        </div>

        ))}
        <hr style={{ borderTop: '1px solid #ccc', margin: '20px 0' }} />
         <div style={{ textAlign: 'right', fontSize: '14px' }}>
        <span>No. of items: {orderData.totalQuantity}</span>
        <span></span><span></span>
        <span>Amount: Rs. {orderData.totalAmount?.toFixed(2)}</span>
        </div>
    </Card>
    <h3>Check out other collections</h3>
    <span>Silk Fabrics</span><span>Mul cotton</span>
    </>)}</>
    );
}
export default PaymentSuccess;
