import classes from './FabricsList.module.css';
import {NavLink} from 'react-router-dom';
function FabricsList({ events }) {
const items= Object.keys(events).map(id => {
  return { uuid: id,id:events[id].id, name:events[id].name, desc:events[id].desc ,details:events[id].details,image:events[id].image};
});
  return (

  <ul>
    <h2>Fabric Store</h2>

      {items.map((item) => (
    <div class={classes.responsive}>
      <div class={classes.gallery}>
      <li key={item.id}>
        <NavLink to={{pathname:`../FabricDetail/${item.id}` }} state={{itemId:`${item.id}` ,itemName:`${item.name}`,itemDetails:`${item.details}`, itemImage:`${item.image}`}} className={({isActive})=>isActive?classes.active:undefined}end>
            <img src={item.image} alt="Cinque Terre" width="300" height="200"/>
        </NavLink>
        </li>
        <div class={classes.desc}>{item.name} {item.desc}</div>

      </div>
    </div>

  ))
}
</ul>

);
}
export default FabricsList;
