import {useEffect, useState, useContext} from 'react';
import { AuthContext } from '../Context/auth-context';
import classes from './MainNavigation.module.css';
import {NavLink} from 'react-router-dom';
import CartButton from '../components/CartButton';

function MainNavigation() {

    const authCtx=useContext(AuthContext);
    useEffect(()=>{
    console.log(authCtx.isLoggedIn);
    },[authCtx.isLoggedIn]);

  return (
  <>
    <ul className={classes.header}>
      <li>
            <NavLink to="/items" ><h1>J'Adore</h1></NavLink>
      </li>
       <li>
            {!authCtx.isLoggedIn &&   (<NavLink to={`/user?mode=login`} className={({isActive})=>isActive?classes.active:undefined}end>Login/Sign up</NavLink>)}
       </li>
       <li>
            {authCtx.isLoggedIn &&  (<NavLink onClick={authCtx.logout} >Logout</NavLink>)}
       </li>
       <li>
               <NavLink to="/" >Wishlist</NavLink>
       </li>
       <li>
               <NavLink >Cart<CartButton/></NavLink>
      </li>
    </ul>
    </>
  );
}

export default MainNavigation;
