import {useDispatch,useSelector} from 'react-redux';
import {useRef, useEffect} from 'react';
import {uiActions} from '../Store/ui_slice';
import classes from './CartButton.module.css';
import Modal from './Modal';
import CartPage from './Cart';
import {useNavigate, redirect, Link} from 'react-router-dom';

const CartButton=()=>{
      const dispatch=useDispatch();
      const cartQuantity=useSelector((state)=>state.cart.totalQuantity);
      const showCart=useSelector((state)=>state.ui.cartIsVisible);
      const modal=useRef();
      const navigate=useNavigate();

     useEffect(()=>{
        console.log("in btn", showCart);
    },[]);
      const toggleCartHandler=()=>{
         dispatch(uiActions.setCartVisibility(true));
        console.log("from btn1");
        //navigate("auth");
        console.log("from btn2");
      };

    function closeModal(){
        console.log("in reset");
        dispatch(uiActions.setCartVisibility(false));
        modal.current.close();
         // dispatch(uiActions.toggle());
     }
     function handleOrder(){
        console.log("in Order");
        dispatch(uiActions.setCartVisibility(false));
        modal.current.close();

     }

      return(
      <>

       <section>
       <Link to="/cart" className={classes.button} onClick={toggleCartHandler}>
            <span className={classes.badge}>{cartQuantity}</span>
       </Link>
       </section>
        </>
      );
    };

export default CartButton;
