import {
  Form,
  Link,
  useSearchParams,
  useActionData,
  useNavigate
} from 'react-router-dom';
import { useForm } from 'react-hook-form';
import {useContext} from 'react';
import {OrderContext} from '../Context/order-context';
import classes from './paymentform.module.css';


const PaymentForm=({onSubmit})=>{
const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset
  } = useForm();

const navigate= useNavigate();
const {item}=useContext(OrderContext);
  //const submitHandler = (data) => {
    //onSubmit(data);
    const submitHandler=async(formData)=>{
        console.log("in ph");
        var options = {
              key: razorpayKey,
              amount: order.amount,
              currency: "INR",
              name: "J'Adore",
              description: "Test Transaction",
              image: "https://example.com/your_logo",
              order_id: order.id,
              handler: async(response)=>{
              const data=null;
                try{
                    const responseData=await fetch('http://localhost:5000/payment/paymentverification',{
                                method:'POST',
                                headers:{'content-type':'application/json'},
                                body:JSON.stringify(response)
                    });
                    let {data}=await responseData.json();

                    if(data){
        //update rzr_payment_id in Orders

                     const payment_response=await fetch('http://localhost:5000/orders',{
                                method:'PUT',
                                headers:{'content-type':'application/json'},
                                body:JSON.stringify({id:orderId,order_id:order.id,rzr_payment_id:data})
                    });

                    if(payment_response){
        //delete from Cart
                    const cartIdString = localStorage.getItem('cartId');
                    const cart_id = cartIdString ? JSON.parse(cartIdString) : null;
                    //const cart_id=JSON.parse(localStorage.getItem('cartId'))
                    console.log(cart_id);
                    if(cart_id){
                        const response=await fetch(
                                `http://localhost:5000/cart/${cart_id}`,
                                { method:'DELETE'}
                              );
                          console.log(response);
                          if(!response)
                          localStorage.removeItem('cartId');
                          dispatch(cartActions.emptyCart());

                    }
                    updateStatus('confirmed');
                    console.log(order.id,data);
                    navigate('/paymentsuccess/'+order.id+'/'+data);
                    }
                   }
                }catch(err){
                    console.log(err);
                    updateStatus('failed');

                }

              },
              prefill: {
                  "name": formData.first_name+" "+formData.last_name,
                  "email": formData.email,
                  "contact": formData.phone
              },
              notes: {
                  "address": "J'Adore Corporate Office"
              },
              theme: {
                  "color": "#8F0555"
              }
        };
         var rzp1 = new window.Razorpay(options);
         rzp1.open();
     };
  

   const resetHandler=()=>{
       navigate('/items');
   };
   return (
   <div className={classes.container}>
     <form  onSubmit={handleSubmit(submitHandler)} className={classes.form}>
    <table><tbody  className={classes.control}>
         <tr >
           <td><label className={classes.label} htmlFor="first_name">First Name</label></td>
           <td><input type="text" id="first_name" name="first_name"
              {...register("first_name", { required: "First name is required." })}  /></td></tr>
  {errors.first_name && <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.first_name.message}</p></td></tr>}

        <tr>
           <td><label htmlFor="last_name">Last Name</label></td>
           <td><input type="text" id="last_name" name="last_name"
             {...register("last_name")}  /></td></tr>
  {errors.last_name && <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.last_name.message}</p></td></tr>}

        <tr><td><label htmlFor="email">Email</label></td>
           <td><input id="email" type="email" name="email"
         {...register("email", {
               required: "Email is required.",
               pattern: {
                 value: /^[^@ ]+@[^@ ]+\.[^@ .]{2,}$/,
                 message: "Email is not valid."}})}/></td></tr>
         {errors.email && <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.email.message}</p></td></tr>}

        <tr><td><label htmlFor="phone">Mobile</label></td>
            <td><input id="phone" type="text" name="phone"
         {...register("phone", {
               required: "Mobile number is required."})}/></td></tr>
         {errors.phone &&  <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.phone.message}</p></td></tr>}
    </tbody>
    </table>
        <div className={classes.action}>
         <button type="reset" onClick={resetHandler} className="button button-flat">
           Cancel
         </button>
         <button  type="submit" className="button" disabled={isSubmitting}>
            Pay Now
         </button>
        </div>
     </form>
    </div>
   );

}
export default PaymentForm;
