    errorElement:<ErrorPage/>,
    children:[
    {
        index:true,
        element:<ItemPage/>,
        loader:itemsLoader
    },
    {
        path:'items',
        element:<ItemPage/>,
        loader:itemsLoader
    },

    {
        path:'fabrics',
        element:<FabricsRootPage/>,
        children:[
         {
            index:true,
            element:<FabricsPage/>,
            loader:fabricsLoader,
         },
         {
            path:':fabricId',
            id:'fabric-detail',
            loader:fabricDetailLoader,
            children:[
                {
                    index:true,
                    element:<FabricDetailPage/>,
                    action:deleteFabricAction,
                 },
                 {
                    path:'edit',
                    element:<EditFabricPage/>,
                    action:manipulateFabricAction,
                 }
            ],
         },
         {
            path:'new',
            element:<NewFabricPage/>,
            action:manipulateFabricAction
         },
         ]
         },
        {
            path:'cart',
            element:<CartPage/>
        },
        {
            path:'user',
            element:<AuthenticationPage/>,
            //action:authenticateUserAction
        },
        {
            path:'payment',
            element:<PaymentPage/>,
        }
    ],
    },
]);*/

const App=()=> {
  const { token, login, logout, userId, isLoggedIn } = useAuth();

let routes;

  if (isLoggedIn) {
    routes = (
      <Switch>
        <Route path="/" exact >
            element:<RootLayout/>,
            errorElement:<ErrorPage/>>
        </Route>
        <Route path="/:userId/places" exact>
          <UserPlaces />
        </Route>
        <Route path="/places/new" exact>
          <NewPlace />
        </Route>
        <Route path="/places/:placeId">
          <UpdatePlace />
        </Route>
        <Redirect to="/" />
      </Switch>
    );
  } else {
    routes = (
      <Switch>
        <Route path="/" exact>
          <Users />
        </Route>
        <Route path="/:userId/places" exact>
          <UserPlaces />
        </Route>
        <Route path="/auth">
          <Auth />
        </Route>
        <Redirect to="/auth" />
      </Switch>
    );
  }

  return (
  <AuthContext.Provider
        value={{
          token: token,
          isLoggedIn: !!token,
          userId: userId,
          login: login,
          logout: logout
        }}
      >
  <Router> {routes} </Router>

  </AuthContext.Provider>
  );
}

export default App;
