import { useState } from 'react';

const SubscriptionForm = () => {
  const [isEmailSub, setEmailSub] = useState(false);
  const [isSmsSub, setSmsSub] = useState(false);
  const [isPushSub, setPushSub] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({
      emailSubscribed: isEmailSub,
      smsSubscribed: isSmsSub,
      pushSubscribed: isPushSub,
      phoneNumber: isSmsSub ? phoneNumber : null,
    });
  };

  return (
    <form onSubmit={handleSubmit} style={styles.form}>
      <h3 style={styles.heading}>Subscribe to Notifications</h3>

      {/* Email Notifications */}
      <div style={styles.checkboxContainer}>
        <input
          type="checkbox"
          checked={isEmailSub}
          onChange={() => setEmailSub(!isEmailSub)}
          id="emailSub"
        />
        <label htmlFor="emailSub" style={styles.label}>Email Notifications</label>
      </div>

      {/* SMS Notifications */}
      <div style={styles.checkboxContainer}>
        <input
          type="checkbox"
          checked={isSmsSub}
          onChange={() => setSmsSub(!isSmsSub)}
          id="smsSub"
        />
        <label htmlFor="smsSub" style={styles.label}>SMS Notifications</label>
        {isSmsSub && (
          <label style={styles.phoneNumber}>
            <input
              type="tel"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              placeholder="Enter your phone number"
              style={styles.input}
            />
          </label>
        )}
      </div>

      {/* Push Notifications */}
      <div style={styles.checkboxContainer}>
        <input
          type="checkbox"
          checked={isPushSub}
          onChange={() => setPushSub(!isPushSub)}
          id="pushSub"
        />
        <label htmlFor="pushSub" style={styles.label}>Browser Notifications</label>
      </div>

      <button type="submit" style={styles.submitButton}>Subscribe</button>
    </form>
  );
};

// Styles
const styles = {
  form: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    width: '100%',
    maxWidth: '400px',
    margin: 'auto',
    textAlign: 'center',
  },
  heading: {
    marginBottom: '20px',
  },
  checkboxContainer: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
  },
  label: {
    marginLeft: '8px',
  },
  phoneNumber: {
    display: 'block',
    marginTop: '10px',
  },
  input: {
    marginTop: '5px',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  submitButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#e44da5',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default SubscriptionForm;
