import {useRef, useState,useEffect} from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate,redirect } from 'react-router-dom';
import classes from './CartItem.module.css';
import { cartActions } from '../Store/cart_slice';
import  '@fortawesome/fontawesome-free/css/all.css';

const CartItem = (props) => {
    const inputQuantity=useRef();
    const totalPriceRef=useRef();
    const dispatch = useDispatch();
    const navigate=useNavigate();
    const {id, type, rate, image, quantity, totalPrice } = props.item;
    const [changedQuantity,setChangedQuantity]=useState(quantity);
    const [changedPrice,setChangedPrice]=useState(totalPrice);


     useEffect(()=>{
        setChangedQuantity(quantity);
        setChangedPrice(totalPrice);
        inputQuantity.current.value=changedQuantity;
        totalPriceRef.current.value=changedPrice;
    },[changedQuantity]);

  const updateCart=()=>{
    console.log(id,changedQuantity);
    dispatch(cartActions.addToCart({id,quantity:inputQuantity.current.value}));
  }

  const changeQuantity=(e)=>{
  if(inputQuantity.current.value<1) {
        alert("Quantity has to be minimum 1 meter");
        console.log(changedQuantity, quantity)
        //setChangedQuantity(quantity);
        e.value=changedQuantity;
        return;
    }
    setChangedQuantity(inputQuantity.current.value)
    e.value=changedQuantity;
    setChangedPrice(inputQuantity.current.value*rate);
    dispatch(cartActions.changeCartItem({item:{
                                                 id,
                                                 type,
                                                 rate,
                                                 image,
                                                 quantity}}));
    }

  const removeEntryHandler = () => {
      inputQuantity.current.value=0;
      dispatch(cartActions.removeFromCart({id}));
   };


  /*const removeItemHandler = () => {
    inputQuantity.current.value-=0.5;
    changeQuantity(document.getElementById("qty{id}"));
  };

  const addItemHandler = () => {
    inputQuantity.current.value=parseFloat(inputQuantity.current.value)+ parseFloat(0.5);
    changeQuantity(document.getElementById("qty{id}"));
    /*dispatch(
      cartActions.addToCart({item:{
        id,
        type,
        rate,
        image,
        quantity:0.5 }})
    );

  };*/

  return (
    <li  className={classes.item}>
        <div className={classes.itemdet}>
            <div>
                <img src={image}/>
            </div>
            <div>
                 <h3>{type}</h3>
                  <div className={classes.itemprice}>(Rs.{rate}/meter)</div>
            </div>

            <div className={classes.quantity}>
                  x <span><input type="text" id="qty{id}" required onBlur={changeQuantity} ref={inputQuantity} /></span>
            </div>
            <div ref ={totalPriceRef} id="tot{id}" className={classes.price}>
                  Rs.{totalPrice}
            </div>
            <div><i class="fa-regular fa-trash-can" onClick={removeEntryHandler}></i></div>
        </div>


    </li>
  );
};

export default CartItem;
