import {useNavigate} from 'react-router-dom';
import { useForm, Controller } from 'react-hook-form';
import Select from 'react-select';
import classes from './fabricform.module.css';


const FabricForm=({onSubmit})=>{
const category = [
  { value: "fabrics", label: "Fabrics" },
  { value: "sarees", label: "Sarees" },
  { value: "lehengas", label: "Lehengas" },
  { value: "salwarsuit", label: "Salwar Suits" }
];
const {
    control,
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset
  } = useForm();

const navigate= useNavigate();
  const submitHandler = (data) => {
    onSubmit(data);
  };

   const resetHandler=()=>{
        console.log("in reset");
        navigate('/fabrics');
   };
   return (
   <div className={classes.container}>
        <h2>Item Details</h2>
     <form  onSubmit={handleSubmit(submitHandler)} className={classes.form}>
    <table><tbody  className={classes.control}>
         <tr >
           <td><label className={classes.label} htmlFor="category">Category type</label></td>
           <td><Controller
                           name="category"
                           control={control}
                           rules={{ required: true }}
                           render={({ field }) => (
                             <Select {...field}  options={category} isMulti={false} className={classes.select}
                             styles={{
                                 option: (provided) => ({
                                   ...provided,
                                   color: '#96075a',// Change text color for all options
                                    //maxHeight:'50px',
                                    height:'10px'
                                 }),}}
                                 style={{maxHeight:'50px'}}/>
                           )}
                         /></td></tr>
  {errors.category && <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.category.message}</p></td></tr>}

        <tr>
           <td><label htmlFor="sub_category">Sub-category</label></td>
           <td><input type="text" id="sub_category" name="sub_category" placeholder="Silk/Linen/Chiffon/Georgette etc."
             {...register("sub_category", { required: "Sub-category type is required." })}  /></td></tr>
  {errors.sub_category && <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.sub_category.message}</p></td></tr>}

        <tr><td><label htmlFor="type">Type</label></td>
           <td><input id="type" type="text" name="type" placeholder="Plain/Printed/Digital Print etc."
         {...register("type", {
               required: "Type is required."})}/></td></tr>
         {errors.type && <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.type.message}</p></td></tr>}

        <tr><td><label htmlFor="imageurl">Image URL</label></td>
            <td><input id="imageurl" type="text" name="imageurl" placeholder="Enter url of the image to be displayed"
         {...register("imageurl", {
               required: "Image URL is required."})}/></td></tr>
         {errors.imageurl &&  <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.imageurl.message}</p></td></tr>}

         <tr><td><label htmlFor="desc">Description</label></td>
              <td><input id="desc" type="textarea" name="desc"
              placeholder="ex. Light weight, pink chiffon fabric with digital print"
            {...register("desc", {
                  required: "Description is required."})}/></td></tr>
            {errors.type && <tr><td></td><td colspan="2"><p className={classes.errorMsg}>{errors.type.message}</p></td></tr>}
    </tbody>
    </table>
        <div className={classes.action}>
         <button type="reset" onClick={resetHandler} className="button button-flat">
           Cancel
         </button>
         <button  type="submit" className="button" disabled={isSubmitting}>
            Add
         </button>
        </div>
     </form>
    </div>
   );

}
export default FabricForm;
