import { useParams } from "react-router-dom";
import {OrderContext} from '../Context/order-context';
import {useContext} from 'react';
import Card from '../components/card';
const PaymentSuccess = () => {

    const {reference} = useParams();
    const {item,totalQuantity,totalAmount}=useContext(OrderContext);

    return (
    <>
    <h2>Order successfully placed !</h2>
    <Card >
            {item.map((itm)=>(

        <div style={{ display: 'flex'}}>
        <img src={itm.image} style={{ width: '100px', height: '100px' }}  alt="" />
        <span>{itm.type}
            <div >
                 Rs. {itm.rate?.toFixed(2)}
               X {totalQuantity}pcs
            </div>
        </span>
        <span>Amount<div>Rs {totalAmount?.toFixed(2)}</div></span>
        </div>
        ))}
    </Card>
    <h3>Check out other collections</h3>
    <span>Silk Fabrics</span><span>Mul cotton</span>
    </>
    );
}
export default PaymentSuccess;
