import { useState, useEffect} from 'react';
import {useDispatch} from 'react-redux';
import {useParams, redirect} from 'react-router-dom';
import classes from './FabricItem.module.css';
import FabricItemOrder from './FabricItemOrder';
import FabricItem from './FabricItem';
import FabricsList from './FabricsList';
import {uiActions} from '../Store/ui_slice';

function FabricDetailPage(){
const [fabric,setFabric]=useState([]);
const [fabrics,setFabrics]=useState([]);
const dispatch=useDispatch();
const {fabricId}=useParams();

 useEffect(()=>{
        const loadFabric=async(dispatch)=>{
        try{
            const response=await fetch('http://localhost:5000/fabrics/'+fabricId);
            if(! response.ok){
                throw Error('Could not load selected fabric');
            }
            const resData=await response.json();
            console.log(resData);
            setFabric(resData);
        }
        catch(error){
            console.log(error);
             /*dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: 'Could not load selected fabric',
              })
            );*/
              console.log(error);
        }
    }
    const loadFabrics=async(dispatch)=>{
        try{
            const response=await fetch('http://localhost:5000/store'+fabric.category);
            if(! response.ok)
                throw Error('Could not load fabrics');

            const resData=await response.json();
            setFabrics(resData);
        }catch(error){
           /*dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: error.message,
              })
            );*/
          console.log(error);
        }
    }
    loadFabric();
    loadFabrics();

     return() => {
       dispatch(uiActions.clearNotification());
     }
 },[fabricId,dispatch]);

 return(<>
 {fabric && < FabricItem item={fabric}/>}
 {fabrics && < FabricsList list={fabrics} category={fabric.category}/>}

 </>);
}

export default FabricDetailPage;






