import { useEffect, useState } from 'react';

import FabricsList from '../components/FabricsList';

function FabricsPage() {
  const [isLoading, setIsLoading] = useState(false);
  const [fetchedEvents, setFetchedEvents] = useState();
  const [error, setError] = useState();

  useEffect(() => {
    async function fetchEvents() {
      setIsLoading(true);
      const response = await fetch('https://mystore-c776c-default-rtdb.firebaseio.com/Fabrics.json');

      if (!response.ok) {
        setError('Fetching data failed.');
      } else {
        const resData = await response.json();
        setFetchedEvents(resData);
      }
      setIsLoading(false);
    }

    fetchEvents();
  }, []);
  return (
    <>
      <div style={{ textAlign: 'center' }}>
        {isLoading && <p>Loading...</p>}
        {error && <p>{error}</p>}
      </div>
      {! fetchedEvents && <p>No data</p>}
      {!isLoading && fetchedEvents && <EventsList events={fetchedEvents} />}
    </>
  );
}

export default EventsPage;
