function FabricDetailPage(){
    const params=useParams();
    let {fabricId}=useParams();
console.log("id="+fabricId);
      const [item, setItem] = useState();

      useEffect(() => {
      console.log(`'http://localhost:5000/fabrics',{params:{fabric_id:${fabricId}}}`);
        axios.get('http://localhost:5000/fabrics',{params:{fabric_id:1}})
          .then(response => setItem(response.data))
          .catch(error => console.error(error));
      }, [fabricId]);
console.log(item);
  return (

    <div className={classes.disp}>
            <div>
                <img src={item.image}/>

                <h2>{item.colour}</h2>
            </div>
            <div >
                <FabricItemOrder/>
            </div>
        </div>
  );
}

export default FabricDetailPage;
