import { useState, useContext } from 'react';
import { AuthContext } from '../Context/auth-context';

const SubscriptionForm = () => {
  const [isEmailSub, setEmailSub] = useState(false);
  const [isSmsSub, setSmsSub] = useState(false);
  const [isPushSub, setPushSub] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');
  const {userId}=useContext(AuthContext);

  const handleSubmit =async (e) => {

    e.preventDefault();

    const notifications={
      emailSubscribed: isEmailSub,
      smsSubscribed: isSmsSub,
      pushSubscribed: isPushSub,
      phoneNumber: isSmsSub ? phoneNumber : null,
    };
    console.log(notifications);
    const response=await fetch(`http://localhost:5000/user/notifications`,
          {
              method: 'PUT',
              headers:{'content-type':'application/json'},
              body:JSON.stringify({
                id:userId,
                notifications:notifications})
          });
      console.log(response);
  };

  return (
    <form onSubmit={handleSubmit} style={{ width: '100%', textAlign: 'left' }}>
      <h3 style={{textAlign:'center',marginBottom:'40px',backgroundColor:'pink'}}>Subscribe to Notifications</h3>

      {/* Email Notifications */}
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'left', marginBottom: '10px' }}>
        <input
          type="checkbox"
          checked={isEmailSub}
          onChange={() => setEmailSub(!isEmailSub)}
          id="emailSub"
        />
        <label htmlFor="emailSub" style={{ marginLeft: '10px' }}>Email Notifications</label>
      </div>

      {/* SMS Notifications */}
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'left', marginBottom: '10px' }}>
        <input
          type="checkbox"
          checked={isSmsSub}
          onChange={() => setSmsSub(!isSmsSub)}
          id="smsSub"
        />
        <label htmlFor="smsSub" style={{ marginLeft: '10px' }}>SMS Notifications</label>
      </div>

      {/* Phone Number Input */}
      {isSmsSub && (
        <div style={{ marginLeft: '500px', marginBottom: '10px' }}>
          <label htmlFor="phoneNumber" style={{ marginBottom: '5px', display: 'block' }}>Enter your Phone Number:</label>
            <input
              type="tel"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              placeholder="Enter your phone number"
              style={{ flex: '1', padding: '5px' }}
            />
      </div>
        )}
      {/* Push Notifications */}
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'left', marginBottom: '10px' }}>
        <input
          type="checkbox"
          checked={isPushSub}
          onChange={() => setPushSub(!isPushSub)}
          id="pushSub"
        />
        <label htmlFor="pushSub" style={{ marginLeft: '10px' }}>Browser Push Notifications</label>
      </div>
       <div style={{textAlign:'center',marginTop:'20px'}} >   <button type="submit" style={{ marginTop: '20px',
                                              padding: '10px 20px',
                                              backgroundColor: '#e44da5',
                                              color: '#fff',
                                              border: 'none',
                                              borderRadius: '5px',
                                              cursor: 'pointer'}}>Subscribe</button></div>

    </form>
  );
};

export default SubscriptionForm;
