import {useEffect, useState, useRef} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import classes from './Cart.module.css';
import { sendCartData, fetchCartData } from '../Store/cart_actions';
import {uiActions} from '../Store/ui_slice';
import Card from './card';
import CartItem from './CartItem';
import {useNavigate,redirect} from 'react-router-dom';
import Modal from './Modal';

let isInitial = true;

const CartPage=()=>{
    const dispatch=useDispatch();
    const cart = useSelector((state) => state.cart);
    const cartItems=useSelector((state) => state.cart.items);
    const [cartId,setCartId]=useState(null);
    const showCart = useSelector((state) => state.ui.cartIsVisible);
    const notification = useSelector((state) => state.ui.notification);
    const modal=useRef();
    const navigate=useNavigate();
    let isInitial=true;


     useEffect(()=>{
     if(isInitial){
          dispatch(fetchCartData(cart));
          isInitial=false;
      }
      showCart && modal.current.open();
     }, [dispatch, showCart]);


      useEffect(() => {
      //showCart && modal.current.open();
        if (cart.changed) {
console.log("sending cart");
setCartId(localStorage.getItem('cart_id'));
console.log("cart id:",cartId);
          dispatch(sendCartData(cart,cartId));
         }
      }, [cart, dispatch]);



    function handleReset(){

        dispatch(uiActions.setCartVisibility(false));
        modal.current.close();
         // dispatch(uiActions.toggle());
     }
     function handleOrder(){
        dispatch(uiActions.setCartVisibility(false));
        modal.current.close();
        navigate('/user?mode=login&order=open');
      }



       return(
       <>
        <Modal ref={modal} onReset={handleReset} onOrder={handleOrder} >
        <h2>My Cart</h2>

        <div className={classes.empty}>
            <span></span>
            <span></span>
            <span><h3>Quantity</h3></span>
            <span><h3>Sub-Total</h3></span>
            <span></span>
        </div>

        <ul>
            {cartItems.map((item)=>(
                <CartItem
                    key={item.id}
                   item={{
                        id:item.id,
                        type:item.type,
                        rate:item.rate,
                        image:item.image,
                        quantity:item.quantity,
                        totalPrice:item.totalPrice
                        }}
                />
            ))}
        </ul>
        <div>
        <button onClick={handleReset}>Continue Shopping</button>
        <button onClick={handleOrder}>Order Now</button>
        </div>
        </Modal>
        </>
   );

    };
    export default CartPage;
