  console.log("ini"+isInitial);
        if (isInitial) {
              isInitial=false;
              return;
         }
        if (cart.changed) {
    console.log("sending crt");
              dispatch(sendCartData(cart));
            }
      }, [cart, dispatch]);*/

       return(
   <Card>
        <h2>My Cart</h2>
        <ul>
            {cartItems.map((item)=>(
                <CartItem
                    key={item.id}
                   item={{
                        id:item.id,
                        type:item.type,
                        rate:item.rate,
                        quantity:item.quantity,
                        totalPrice:item.totalPrice
                        }}
                />
            ))}
        </ul>
    </Card>
   );

    };
    export default CartPage;
