import { createSlice } from '@reduxjs/toolkit';
const initialCartState={ items:[],totalQuantity:0,changed:false };

const cartSlice=createSlice({
    name:'cart',
    initialState:{ items:[],totalQuantity:0,changed:false },
    reducers:{
        replaceCart(state,action){
            state.totalQuantity=action.payload.totalQuantity;
            state.items=action.payload.items;
        },
        addToCart(state,action){
            const newItem=action.payload;
            const existingItem=state.items.find((item)=>item.id===newItem.id);
            state.totalQuantity++;
            state.changed=true;

            if(!existingItem){
               state.items.push({
                id:newItem.id,
                quantity:1,
                rate:newItem.rate,
                totalPrice:newItem.rate,
                name:newItem.title,
            })
            }else{
                existingItem.quantity++;
                existingItem.totalPrice=existingItem.totalPrice+newItem.rate;
            }
        },
        removeFromCart(state,action){
            const id=action.payload;
            const existingItem=state.items.find((item)=>item.id === id);
            state.totalQuantity--;
            state.changed=true;
            if(existingItem.quantity===1){
                state.items=state.items.filter((item)=>item.id !== id);
            }else{
               existingItem.quantity--;
               existingItem.totalPrice=existingItem.totalPrice-existingItem.rate;
            }
        },
    }
});


export const cartActions=cartSlice.actions;
export default cartSlice;
