import { useEffect, useState} from 'react';
import {useParams} from 'react-router-dom';
import {useDispatch} from'react-redux';
import {uiActions } from '../Store/ui_slice';
import FabricsList from '../components/FabricsList';

function FabricsPage() {
  const [fabrics, setFabrics] = useState([]);
  const dispatch= useDispatch();
  const {category,sub_category} = useParams();
  console.log(category, sub_category);
  if(sub_category){}
    const fetchFabrics=async(dispatch)=>{
        try{
        console.log("in fabrics useeffect");

              const response = await fetch('http://localhost:5000/'+category);
              if (!response.ok) {
                throw new Error('Fetching Fabrics failed')
              }
              const resData = await response.json();
              console.log(resData);
              setFabrics(resData);
          }catch(error){
              /*dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: error.message,
              })
            );*/
            console.log(error.message);
           }
        }

  useEffect(() => {
        fetchFabrics();
        return()=>{
            dispatch(uiActions.clearNotification());
        }
    },[dispatch, category]);
    return (
    <>{fabrics && <FabricsList list={fabrics} category={category} updateFabricList={fetchFabrics}/>}</>);
  };

    export default FabricsPage;
