import {useParams,useLocation} from 'react-router-dom';
import FabricItem from '../components/FabricItem';
function FabricDetailPage(){
    const params=useParams();
    const location=useLocation();
    const { state }=location;
console.log(state.itemName);
    return(
    <>
<div>
    <h1>{state.itemName}</h1>
    <img src={state.itemImage}/>
    <p>{state.itemDetails}</p>
    </div>
    <FabricItem item={state}/>
</>
    );
}

export default FabricDetailPage;
