import { createSlice } from '@reduxjs/toolkit';

const cartSlice=createSlice({
    name:'cart',
    initialState:{
      items:[],
      total_quantity:0
    },
    reducers:{
        addToCart(state){},
        removeFromCart(state){}
    }
});


export const cartActions=cartSlice.actions;
export default cartSlice;
