import {useEffect, useState, useContext} from 'react';
import { AuthContext } from '../Context/auth-context';
import classes from './MainNavigation.module.css';
import {NavLink} from 'react-router-dom';
import CartButton from '../components/CartButton';

function MainNavigation() {

    const authCtx=useContext(AuthContext);
    useEffect(()=>{
    console.log(authCtx.isLoggedIn);
    },[authCtx.isLoggedIn]);

  return (
    <header className={classes.header}>
      <ul className={classes.nav}>
      <li>
            <NavLink to="/" ><i class="fa-solid fa-magnifying-glass"></i></NavLink>
      </li>
      <li>
            <NavLink to="/homepage" ><h1 className={classes.logo}>J'Adore</h1></NavLink>
      </li>
       <li className={classes.right}>
            {!authCtx.isLoggedIn && (<NavLink to={`/user?mode=login`} className={({isActive})=>isActive?classes.active:undefined}end><i class="fa-regular fa-user"></i></NavLink>)}
            {authCtx.isLoggedIn &&  (<NavLink onClick={authCtx.logout} ><i class="fa-regular fa-user"></i></NavLink>)}
            <NavLink to="/" ><i class="fa-regular fa-heart"></i></NavLink>
            <NavLink ><CartButton/></NavLink>
      </li>
    </ul>
    </header>
  );
}

export default MainNavigation;
