    {
    path:'/',
    element:<RootLayout/>,
    errorElement:<ErrorPage/>,
    children:[
    {
        index:true,
        element:<ItemPage/>,
        loader:itemsLoader
    },
    {
        path:'items',
        element:<ItemPage/>,
        loader:itemsLoader
    },

    {
        path:'fabrics',
        element:<FabricsRootPage/>,
        children:[
         {
            index:true,
            element:<FabricsPage/>,
            loader:fabricsLoader,
         },
         {
            path:':fabricId',
            id:'fabric-detail',
            loader:fabricDetailLoader,
            children:[
                {
                    index:true,
                    element:<FabricDetailPage/>,
                    action:deleteFabricAction,
                 },
                 {
                    path:'edit',
                    element:<EditFabricPage/>,
                    action:manipulateFabricAction,
                 }
            ],
         },
         {
            path:'new',
            element:<NewFabricPage/>,
            action:manipulateFabricAction
         },
         ]
         },
        {
            path:'cart',
            element:<CartPage/>
        },
        {
            path:'user',
            element:<AuthenticationPage/>,
            //action:authenticateUserAction
        },
        {
            path:'payment',
            element:<PaymentPage/>,
        }
    ],
    },
]);*/

const App=()=> {
  const { token, login, logout, userId, isLoggedIn } = useAuth();
  const location = useLocation();
  const dispatch = useDispatch();
  useEffect(() => {
        dispatch(uiActions.clearNotification());
    }, [location, dispatch]);

let routes;
console.log(isLoggedIn);
  if (isLoggedIn) {
    routes = (
      <Routes>
        <Route path="/" element={ <RootLayout/>} >
        <Route index={true} path="/items" element={<ItemPage />}  />
        <Route path="/fabrics" element={<FabricsPage />} />
        <Route path="/fabrics/:fabricId" element={<FabricDetailPage />} />
        <Route path="/fabrics/:fabricId/:edit" element={<EditFabricPage />} />
        <Route path="/fabrics/:new" element={<NewFabricPage />} />
        <Route path="/cart" element={<CartPage />}  />
        <Route path="/user" element={<AuthenticationPage />}  />

        {isLoggedIn ? (
              <Route path="/*" element={<Navigate to="/" replace />} />
            ) : (
              <Route path="/*" element={<Navigate to="/auth" replace />} />
            )}
        </Route>
    </Routes>
    );
  }
  else{
     routes = (
      <Routes>
        <Route path="/" element={ <RootLayout/>} >
        <Route index={true} path="/items" element={<ItemPage />}  />
        <Route path="/fabrics" element={<FabricsPage />} />
        <Route path="/fabrics/:fabricId" element={<FabricDetailPage />} />
        <Route path="/cart" element={<CartPage />}  />
        <Route path="/user" element={<AuthenticationPage />}  />
        <Route path="/*" element={<Navigate to="/auth" replace />} />
        {isLoggedIn ? (
          <Route path="/*" element={<Navigate to="/" replace />} />
        ) : (
          <Route path="/*" element={<Navigate to="/auth" replace />} />
        )}
        </Route>
     </Routes>
    );
  }
  return (
  <AuthContext.Provider
        value={{
          token: token,
          isLoggedIn: isLoggedIn,
          userId: userId,
          login: login,
          logout: logout
        }}
  >
  <Router> {routes} </Router>

  </AuthContext.Provider>
  );
}

export default App;
