import {useContext, useState, useEffect} from 'react';
import {useNavigate, NavLink} from 'react-router-dom';
import { AuthContext } from '../Context/auth-context';
import moment from 'moment';
import classes from './ticket.module.css';

const Notifications=()=>{
 const {isAdmin,superAdmin, isLoggedIn, token}  =useContext(AuthContext);
 const [notifications,setNotifications] = useState(null);
 const navigate=useNavigate();
useEffect(()=>{
    const fetchNotifications=async()=>{
    try{
        let response;
        if(isAdmin)
             response=await fetch('http://localhost:5000/notifications',{
               method:'GET',
               headers:{
                   'Content-Type':'application/json',
                    'Authorization':'Bearer '+ token}
               });
        else
             response=await fetch('http://localhost:5000/notifications/user',{
                method:'GET',
                headers:{
                    'Content-Type':'application/json',
                     'Authorization':'Bearer '+ token}
                });

        console.log(response);
        const {notifications}=await response.json();
        console.log(notifications);
        if(notifications)
            setNotifications(notifications);
    }
    catch(error){
        console.log(error);
    }
    }


    /*const deleteFabric = async(dispatch)=>{
      try{
           const response=await fetch(
                    `http://localhost:5000/fabrics/${id}`,
                    { method:'DELETE',
                      headers:{Authorization:'Bearer '+ authCtx.token}
                    }
                  );
           if(! response.ok)
                throw new Error("Could not delete the Fabric");
           const resData = await response.json();
           console.log(resData);
           updateFabricList();
           navigate('/store/'+category);
        }
        catch(error){
            console.log(error);
            }
        }
      deleteFabric(dispatch);
   }*/


   fetchNotifications();
},[]);

const addNotificationHandler = () => {
       navigate(`/notifications/new`);
   }

 const removeEntryHandler = (event) => {
  event.preventDefault(); // Prevent the default anchor tag behavior
   event.stopPropagation(); // Stop the click from propagating to the NavLink
    alert('remove');
    return;
 }
 return(<>
    {
    isLoggedIn && isAdmin && (
        <div style={{textAlign:'center',marginBottom:'40px',marginTop:'40px'}}>
        <button onClick={addNotificationHandler} className={classes.button} >New Notification</button>
        </div>)
    }
    <ul>
      {notifications && notifications.map((notification) => (

         <li key={notification._id}>
            <NavLink style={{textDecoration:'none'}} to={{pathname:`/notification/edit/${notification._id}` }}  className={classes.ticket}>
                <div style={{minWidth:'100px'}}>{notification.notificationMsg}</div>
            </NavLink>
          </li>))}
    </ul>

    </>
 )
}
export default Notifications;
