import {useEffect} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import classes from './Cart.module.css';l
import { sendCartData, fetchCartData } from '../Store/cart_actions';
import Card from './card';
import CartItem from './CartItem';
import ReactDOM from 'react';
let isInitial = true;

const CartPage=()=>{
    const dispatch=useDispatch();
    const cart = useSelector((state) => state.cart);
    const cartItems=useSelector((state) => state.cart.items);
    const showCart = useSelector((state) => state.ui.cartIsVisible);
    const notification = useSelector((state) => state.ui.notification);

     useEffect(()=>{
          console.log("fetching crt");
          dispatch(fetchCartData(cart));
         console.log("after fetching crt");
         isInitial=false;

     }, [dispatch]);


      useEffect(() => {
        if (cart.changed) {
    console.log("sending crt");
          dispatch(sendCartData(cart));
         }
      }, [cart, dispatch]);

       return(
       <Card>
       <dialog className={classes.cart_modal}>
        <h2>My Cart</h2>
        <ul>
            {cartItems.map((item)=>(
                <CartItem
                    key={item.id}
                   item={{
                        id:item.id,
                        type:item.type,
                        rate:item.rate,
                        quantity:item.quantity,
                        totalPrice:item.totalPrice
                        }}
                />
            ))}
        </ul>
        <form method="dialog">
            <button>Close</button>
        </form>
        </dialog>
    </Card>
   );

    };
    export default CartPage;
