import {useState,useEffect,useRef,useContext} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {useNavigate} from 'react-router-dom';
import classes from './FabricItemOrder.module.css';
import {cartActions} from '../Store/cart_slice';
import {uiActions} from '../Store/ui_slice';
import { sendCartData } from '../Store/cart_actions';
import CartPage from './Cart';
import Modal from './Modal';
import {OrderContext} from '../Context/order-context';

const FabricItemOrder=(props)=>{
    const [quantity,setQuantity]=useState(0);
    const navigate=useNavigate();
    const {createOrder, status}=useContext(OrderContext);
    const {id,category,type, desc, details, rate,image,created_by,stock}=props.item;
    console.log(created_by, stock);
    const dispatch=useDispatch();
    const cart=useSelector((state)=>state.cart);
    const showCart=useSelector((state)=>state.ui.cartIsVisible);
    dispatch(uiActions.clearNotification());
    const modal=useRef();
    const [stockMsg, setStockMsg]= useState(null);
    //const isInitial=useSelector((state) => state.cart.isInitial);
    //let stockMsg="";
    useEffect(() => {

        if(stock<=2 && category!=='fabrics')
            setStockMsg(`Last ${stock} remaining`);
        if(stock<=2 && category==='fabrics')
            setStockMsg(`Only ${stock}meter(s) in stock`);
        if(stock<=0)
            setStockMsg("Out of stock");
      }, [stock]);

    const handleChange=(e)=>{
        e.preventDefault();
        setQuantity(e.target.value);
    };

    const addToCart=(e)=>{
        e.preventDefault();
        if(stock===0){
            alert("Insufficient stock!")
            return;
        }
       if(Number(quantity)===0 || quantity.trim()===''){
            alert("Enter quantity");
            return;
       }
        dispatch(cartActions.addToCart({item:{
                                                id,
                                                type,
                                                rate,
                                                image,
                                                created_by,
                                                category,
                                                quantity:quantity}}));
       // dispatch(cartActions.findCartTotal());
        const updateStock=async(id, quantity)=>{
        const stock_response=await fetch('http://localhost:5000/store',{
                            method:'PUT',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({id:id,quantity:quantity})
            });
            if(stock_response){
                const updatedStock=await stock_response.json();
                console.log(updatedStock.data);
            }

        }
        console.log(id,quantity);
        updateStock(id, quantity);
        dispatch(uiActions.setCartVisibility(true));
        setTimeout(() => {
            console.log(status);
            if (!status) {
              updateStock(id, -quantity);
            }
          }, 360000); // 1 minute in milliseconds
        navigate('/Cart');
    };

    const checkoutHandler=async(e)=>{
    e.preventDefault();
    if(stock===0){
                alert("Insufficient stock!")
                return;
    }
    if(Number(quantity)===0 || quantity.trim()===''){
     alert("Enter quantity");
     return;
    }
    const amount=rate*quantity;
    const order_item={id,type,rate,image,quantity,amount,created_by,category};
        //let order_items=[];
        //order_items.push(order_item);
    try{
  //=============================================
    //check Stock in store for order_item



  //=============================================
        const response = await fetch('http://localhost:5000/orders',{
                        method:'POST',
                        headers:{'content-type':'application/json'},
                        body:JSON.stringify({items:order_item,totalQuantity:quantity,totalAmount:amount})
        });
        const {orderId} = await response.json();

    console.log(order_item);
    createOrder([order_item],order_item.length,rate*quantity,orderId);
    navigate('/order/'+orderId);

    }
    catch(error){
        console.log(error.message);
        dispatch(
                uiActions.showNotification({
                status: 'error',
                title:  'Error!',
                message: 'Could not place Order. Please try again',
              })
            );
        }
  };
return(
<>

<section className={classes.section}>
    <form>
    {category && category.toLowerCase()==='fabrics' && (
    <div className={classes.disp}>
    <div>{desc}</div>
    <div><span>Price:</span><span>{rate} </span><span>Rs/meter</span></div>
    <div><span> Quantity:</span><span><input style={{width:'100px'}} onChange={handleChange} name="qty" type="text"  /></span><span>meter</span></div>
     <div><span style={{fontSize:'12px'}}>Minimum Order 1 meter</span></div>
    </div>)}

    {category && category.toLowerCase()!=='fabrics' && (
    <div className={classes.disp}>
    <div>{desc}</div>
    <div><span>Price:</span><span>Rs</span><span>{rate}/- </span></div>
    <div><span> Quantity:</span><span><input style={{width:'100px'}} onChange={handleChange} name="qty" type="text"  /></span></div>
    </div>)}



    {stockMsg && <div className={classes.message}>{stockMsg}</div>}

    {Array.isArray(details) &&
     <div className={classes.details}>
        <span>{details[0]}</span>
        <span>{details[1]}</span>
     </div>}
     
      <div className={classes.actions}>

       {(stock!==0) && (<>
             <span><button onClick={addToCart}>ADD TO CART</button></span>
             <span><button onClick={checkoutHandler}>BUY IT NOW</button></span></>)}

       {(stock===0) && (<>
             <span><button disabled={false} onClick={addToCart}>ADD TO CART</button></span>
             <span><button disabled={false} onClick={checkoutHandler}>BUY IT NOW</button></span></>)}
         </div>
    </form>
    </section>
</>
);
}
export default FabricItemOrder;
