import {useEffect, useRef} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import classes from './Cart.module.css';
import { sendCartData, fetchCartData } from '../Store/cart_actions';
import {uiActions} from '../Store/ui_slice';
import Card from './card';
import CartItem from './CartItem';
import {useNavigate,redirect} from 'react-router-dom';
import Modal from './Modal';

let isInitial = true;

const CartPage=()=>{

    const dispatch=useDispatch();
    const cart = useSelector((state) => state.cart);
    const cartItems=useSelector((state) => state.cart.items);
    const cartTotalItems=useSelector((state)=> state.cart.totalQuantity);
    const showCart = useSelector((state) => state.ui.cartIsVisible);
    const notification = useSelector((state) => state.ui.notification);
    const modal=useRef();
    const navigate=useNavigate();
    let isInitial=true;


    useEffect(() => {
        if (cart.changed) {
          dispatch(sendCartData(cart));
         }
      }, [cart]);


     useEffect(()=>{
     if(isInitial){
          dispatch(fetchCartData(cart));
          isInitial=false;
      }
      console.log(cartTotalItems);
      showCart && cartTotalItems && modal.current.open();
     }, [dispatch, showCart]);






    function handleReset(){

        dispatch(uiActions.setCartVisibility(false));
        modal.current.close();
     }

     function handleOrder(){
        dispatch(uiActions.setCartVisibility(false));
        modal.current.close();
        navigate('/user?mode=login&order=open');
      }



       return(
       <>

        <Modal ref={modal} onReset={handleReset} onOrder={handleOrder} >
        <h2>My Cart</h2>

        <div className={classes.empty}>
            <span></span>
            <span></span>
            <span><h3>Quantity</h3></span>
            <span><h3>Sub-Total</h3></span>
            <span></span>
        </div>

        <ul>
            {cartItems.map((item)=>(
                item.quantity>0 &&
                <CartItem
                    key={item.id}
                   item={{
                        id:item.id,
                        type:item.type,
                        rate:item.rate,
                        image:item.image,
                        quantity:item.quantity,
                        totalPrice:item.totalPrice
                        }}
                />
            ))}
        </ul>
        <div>
        <button onClick={handleReset}>Continue Shopping</button>
        <button onClick={handleOrder}>Order Now</button>
        </div>
        </Modal>

        </>
   );

    };
    export default CartPage;
