import {useEffect, useRef, useContext} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import classes from './Cart.module.css';
import { sendCartData } from '../Store/cart_actions';
import {uiActions} from '../Store/ui_slice';
import Card from './card';
import CartItem from './CartItem';
import {useNavigate,redirect} from 'react-router-dom';
import Modal from './Modal';
import Notification from './notification';
import {AuthContext} from '../Context/auth-context';
import {OrderContext} from '../Context/order-context';

const CartPage=()=>{

    const dispatch=useDispatch();
    const authCtx=useContext(AuthContext);
    const{item,createOrder}=useContext(OrderContext);
    const cart = useSelector((state) => state.cart);
    const cartItems=useSelector((state) => state.cart.items);
    const isInitial=useSelector((state) => state.cart.isInitial);
    const cartTotalItems=useSelector((state)=> state.cart.totalQuantity);
    const cartAmount=useSelector((state)=> state.cart.totalAmount);
    const showCart = useSelector((state) => state.ui.cartIsVisible);
    const notification = useSelector((state) => state.ui.notification);
    const modal=useRef();
    const navigate=useNavigate();
    //let isInitial=true;


    useEffect(() => {
        /*if(isInitial){
        console.log("fetching cart");
          dispatch(fetchCartData(cart));
         }*/
        if (cart.changed)
         {
        // isInitial=false;
         console.log("in sendcart");
          dispatch(sendCartData(cart));
         }
         if(!cartTotalItems)
                modal.current.close();
         return() => {
           dispatch(uiActions.clearNotification());
         }
      }, [cart,dispatch]);


     useEffect(()=>{


      cartTotalItems && showCart && modal.current.open();
      return() => {
             dispatch(uiActions.clearNotification());
              dispatch(uiActions.setCartVisibility(false));
           }
     }, [dispatch, showCart]);






    function handleReset(){

        dispatch(uiActions.setCartVisibility(false));
        dispatch(uiActions.clearNotification());
        modal.current.close();
        //navigate('/items');
     }

    const handleOrder=async()=>{
        dispatch(uiActions.setCartVisibility(false));
        modal.current.close();
//check stock of each item in cartItems


        try{
            const response = await fetch('http://localhost:5000/orders',{
                            method:'POST',
                            headers:{'content-type':'application/json'},
                            body:JSON.stringify({items:[cartItems],totalQuantity:cartTotalItems,totalAmount:cartAmount})
            });
            const {orderId} = await response.json();
            console.log(orderId);



        createOrder(cartItems,cartTotalItems,cartAmount,orderId);
        console.log(cartItems, item,orderId);
        navigate('/order/'+orderId);
        //navigate('/payment');
        }
        catch(error){
               console.log(error.message);
               dispatch(
                       uiActions.showNotification({
                       status: 'error',
                       title:  'Error!',
                       message: 'Could not place Order. Please try again',
                     })
                   );
        }
    }


       return(<>
        <Modal ref={modal} onReset={handleReset} onOrder={handleOrder} >
        <h2>My Cart</h2>


        <div className={classes.empty}>
            <span></span>
            <span></span>
            <span><h4>Quantity</h4></span>
            <span><h4>Sub-Total</h4></span>
            <span></span>
        </div>

        <div className={classes.cartItemsContainer}>
        <ul>
            {cartItems.map((item)=>(
                item.quantity>0 &&
                <CartItem
                    key={item.id}
                   item={{
                        id:item.id,
                        type:item.type,
                        rate:item.rate,
                        image:item.image,
                        quantity:item.quantity,
                        totalPrice:item.totalPrice,
                        category:item.category
                        }}
                />
            ))}
        </ul>
        </div>
        <div className={classes.summary}>
        <span>Total Items: {cartTotalItems}</span><span>Amount: {cartAmount}</span>
        </div>
        <div className={classes.actions} >
        <button className={classes.button} onClick={handleReset}>Continue Shopping</button>
        <button className={classes.button} onClick={handleOrder}>Order Now</button>
        </div>
        </Modal>
    { !cartTotalItems &&
         (<div className={classes.shop} >
         <button style={{borderRadius:'20px'}} onClick={handleReset}>Start Shopping</button>
         <img style={{height:'100px',width:'100px',borderRadius:'20px'}} src="https://i.pinimg.com/236x/f9/31/39/f931398e98f3566c894b675a1b51c602.jpg"/>
         </div>)
    }
</>
);

  }


    export default CartPage;
