import {useEffect, useRef} from 'react';
import {useSelector,useDispatch} from 'react-redux';
import classes from './Cart.module.css';
import { sendCartData, fetchCartData } from '../Store/cart_actions';
import Card from './card';
import CartItem from './CartItem';
import {useNavigate,redirect} from 'react-router-dom';
import {Modal} from './Modal';

let isInitial = true;

const CartPage=()=>{
    const dispatch=useDispatch();
    const cart = useSelector((state) => state.cart);
    const cartItems=useSelector((state) => state.cart.items);
    const showCart = useSelector((state) => state.ui.cartIsVisible);
    const notification = useSelector((state) => state.ui.notification);
    const modal=useRef();
    const navigate=useNavigate();
const dialog = document.getElementById("modal-root");
//const selectEl = favDialog.querySelector("select");



     useEffect(()=>{
          dispatch(fetchCartData(cart));
          console.log("fetched cart");
//         isInitial=false;

     }, [dispatch]);


      useEffect(() => {
     // modal.current.open();
        if (cart.changed) {
          dispatch(sendCartData(cart));
         }
      }, [cart, dispatch]);



    function handleReset(){
        console.log("in reset of cart");
        //document.getElementById('modal-root').close="true";
//.show=false;
        //dispatch(uiActions.setCartVisibility(false));
        //modal.current.close();
         // dispatch(uiActions.toggle());
     }
     function handleOrder(){
        console.log("in Order of cart");
        //document.getElementById('root').focus();
        //document.getElementById('modal-root').show="false";
        navigate('/auth');


        //dispatch(uiActions.setCartVisibility(false));
        //modal.current.close();
     }
       return(
       <>
        <Modal ref={modal} onReset={closeModal} onOrder={handleOrder} >
        <h2>My Cart</h2>

        <div className={classes.empty}>
            <span></span>
            <span></span>
            <span><h3>Quantity</h3></span>
            <span><h3>Sub-Total</h3></span>
            <span></span>
        </div>

        <ul>
            {cartItems.map((item)=>(
                <CartItem
                    key={item.id}
                   item={{
                        id:item.id,
                        type:item.type,
                        rate:item.rate,
                        image:item.image,
                        quantity:item.quantity,
                        totalPrice:item.totalPrice
                        }}
                />
            ))}
        </ul>
        <div>
        <button onClick={handleReset}>Continue Shopping</button>
        <button onClick={handleOrder}>Order Now</button>
        </div>
        </Modal>
        </>
   );

    };
    export default CartPage;
